    }

    return ret;
}

void initCorrectedInvoiceValues_RU()
{
    PurchLine_W purchLineLocal_W;
    VendInvoiceTrans vendInvoiceTrans;
    VendInvoiceInfoLine infoLineLocal;

    if (vendInvoiceInfoTable_RU.Correct_RU)
    {
        select firstonly purchLineLocal_W
            where purchLineLocal_W.RefReturnInvoiceTrans_W
        exists join infoLineLocal
            where infoLineLocal.PurchLineRecId == purchLineLocal_W.PurchLine &&
                  infoLineLocal.ParmId == vendInvoiceInfoTable.ParmId &&
                  infoLineLocal.TableRefId == vendInvoiceInfoTable.TableRefId;

        if (purchLineLocal_W)
        {
            vendInvoiceTrans = VendInvoiceTrans::findRecId(purchLineLocal_W.RefReturnInvoiceTrans_W);
            vendInvoiceInfoTable_RU.CorrectedInvoiceId_RU = vendInvoiceTrans.InvoiceId;
            vendInvoiceInfoTable_RU.CorrectedInvoiceDate_RU = vendInvoiceTrans.InvoiceDate;

            vendInvoiceInfoTable_DS.initCorrectedInvoiceLines_RU();
            vendInvoiceInfoTable_DS.setCorrFacture_RU();
        }
    }
}

void allowEditCorrectedInvoiceId_RU(boolean _clearValues = false)
{
    if (_clearValues)
    {
        vendInvoiceInfoTable_RU.CorrectedInvoiceId_RU = '';
        vendInvoiceInfoTable_RU.CorrectedInvoiceDate_RU = dateNull();
    }

    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedInvoiceId_RU)).enabled (vendInvoiceInfoTable_RU.Correct_RU);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedInvoiceDate_RU)).enabled(vendInvoiceInfoTable_RU.Correct_RU);
}

