    Set invoiceJourSet = new Set(Types::Int64);
    VendInvoiceJour vendInvoiceJour = vendInvoiceInfoTable.vendInvoiceInfoTable_RU().getOriginalJour();

    invoiceJourSet.add(vendInvoiceJour.RecId);

    factureJour = FactureJour_RU::lookupCorrectedFactureExternalId(_formControl,
                                                                   vendInvoiceInfoTable.InvoiceAccount,
                                                                   FactureTaxCorrectionContragentType_RU::Vendor,
                                                                   invoiceJourSet);

    if (factureJour)
    {
        vendInvoiceInfoTable_RU.CorrectedFactureDate_RU = factureJour.FactureDate;
    }
}

