public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup lookup = SysReferenceTableLookup::newParameters(
        tableNum(PurchBookVATProcessParameters_RU),
        _formReferenceControl,
        true);

    Query query;
    QueryBuildDataSource qbds;
    PurchBookVATProcessParameters_RU selectedRecord;

    lookup.addLookupfield(fieldNum(PurchBookVATProcessParameters_RU, OperationCode));

    query = new Query();
    qbds = query.addDataSource(tableNum(PurchBookVATProcessParameters_RU));

    qbds.addRange(fieldNum(PurchBookVATProcessParameters_RU, ProcessingType)).value(queryValue(PurchBookProcessingType_RU::Standard));

    lookup.parmQuery(query);
    selectedRecord = lookup.performFormLookup();

    return selectedRecord;
}

public void modified()
{
    super();

    if (vendInvoiceInfoTable_RU.vatOnPayment_RU == NoYes::Yes)
    {
        vendInvoiceInfoTable_RU.ProcessVAT = NoYes::No;
        vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, ProcessVAT)).enabled(false);
        vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, PurchBookVATProcessParametersRecId)).enabled(false);
    }
    else
    {
        vendInvoiceInfoTable_RU.ProcessVAT = PurchBookVATProcessParameters_RU::find(vendInvoiceInfoTable_RU.PurchBookVATProcessParametersRecId).ProcessVAT;
        vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, ProcessVAT)).enabled(true);
        vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, PurchBookVATProcessParametersRecId)).enabled(true);
    }
}

