{
    if (isW || isGB || VatDueDateFeature::isEnabled())
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}

public void write()
{
    // a record with an empty FK is not valid
    if ((isW || isGB || VatDueDateFeature::isEnabled()) && vendInvoiceInfoTable_W.VendInvoiceInfoTable != 0)
    {
        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            operationIdChanged = (vendInvoiceInfoTable_W.orig().SalesPurchOperationType_BR != vendInvoiceInfoTable_W.SalesPurchOperationType_BR);
            serviceCodeOnDeliveryAddressChanged = (vendInvoiceInfoTable_W.orig().ServiceCodeOnDlvAddress_BR != vendInvoiceInfoTable_W.ServiceCodeOnDlvAddress_BR);
        }

        // </GBR>

        super();

        if (tableToLineUpdatePrompt_W)
        {
            tableToLineUpdatePrompt_W.runOperation();
            if (tableToLineUpdatePrompt_W.wasLineUpdated())
            {
                vendInvoiceInfoLine_W_ds.reread();
                vendInvoiceInfoLine_W_ds.refresh();
            }
            tableToLineUpdatePrompt_W = null;
        }
    }
}

public void modified()
{
    super();
    vendInvoiceInfoTable_DS.refresh();
    vendInvoiceInfoTable_W_DS.refresh();
}

