public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    FiscalEstablishmentRecId_BR fiscalEstablishmentRecId;
    Query query;
    QueryBuildDataSource qbds;
    Common ret;

    SysReferenceTableLookup sysReferenceTableLookup = SysReferenceTableLookup::newParameters(tableNum(FiscalDocumentType_BR), _formReferenceControl);
    sysReferenceTableLookup.addLookupfield(fieldNum(FiscalDocumentType_BR, FiscalDocumentTypeId));
    sysReferenceTableLookup.addLookupfield(fieldNum(FiscalDocumentType_BR, Name));

    fiscalEstablishmentRecId = conPeek(vendInvoiceInfoTable.getFiscalEstablishment(), 1);

    query = new Query();
    qbds = query.addDataSource(tableNum(FiscalDocumentType_BR));
    qbds.addRange(fieldNum(FiscalDocumentType_BR, FiscalEstablishment_BR)).value(queryValue(fiscalEstablishmentRecId));

    sysReferenceTableLookup.parmQuery(query);

    ret = sysReferenceTableLookup.performFormLookup();

    return ret;
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    FiscalDocumentTypeId_BR fiscalDocumentTypeId;

    // Get the text that the user entered into the control to see if there is a match to display
    fiscalDocumentTypeId = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(FiscalDocumentType_BR, FiscalDocumentTypeId), tableStr(FiscalDocumentType_BR))).value();

    if (fiscalDocumentTypeId)
    {
        ret = FiscalDocumentType_BR::find(fiscalDocumentTypeId, FiscalEstablishment_BR::find(conPeek(vendInvoiceInfoTable.getFiscalEstablishment(),1)).FiscalEstablishmentId);
    }
    else
    {
        ret = super(_formReferenceControl);
    }

    return ret;
}

public boolean validate()
