        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    switch (documentStatus)
    {
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            VendPackingSlipJour::lookupPackingSlipId_W(
                this, VendPackingSlipJour::queryPackingSlipIdForUpdate_W(
                    vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.PurchId, vendInvoiceInfoTable.TableRefId));
            break;
        case DocumentStatus::PackingSlip:
            VendPackingSlipJour::lookupPackingSlipId_W(this, VendPackingSlipJour::queryPackingSlipStorno_LT());
            break;
    }
}

public void jumpRef()
{
    FormRun formRun = new MenuFunction(menuItemDisplayStr(VendPackingSlipJournal), MenuItemType::Display).create();

    FormDataSource formDataSource = formRun.dataSource();
    switch (documentStatus)
    {
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            formDataSource.query(VendPackingSlipJour::queryPackingSlipIdForUpdate_W(vendInvoiceInfoTable.ParmId,
                                                                                    vendInvoiceInfoTable.PurchId,
                                                                                    vendInvoiceInfoTable.TableRefId));
            break;
        case DocumentStatus::PackingSlip:
            formDataSource.query(VendPackingSlipJour::queryPackingSlipStorno_LT());
            break;
    }
    if (vendInvoiceInfoTable_W.PackingSlipIdForUpdate_W)
    {
        QueryBuildDataSource qBd = formDataSource.query().dataSourceTable(tableNum(VendPackingSlipJour));
        QueryBuildRange qBr = qBd.addRange(fieldNum(VendPackingSlipJour,PackingSlipId));
        qBr.value(vendInvoiceInfoTable_W.PackingSlipIdForUpdate_W);
    }
    formRun.run();
    formRun.detach();
}

