}

public void clicked()
{
    if (VendAdvanceBlockApplyWithPaymentScheduleFlight::instance().isEnabled() && purchTable.PaymentSched != "")
    {
        throw error("@AccountsPayable:BlockPrepaymentWithPaymSchedule");
    }

    SpecTrans specTrans;

    select firstonly RecId from specTrans
        where specTrans.SpecTableID == vendInvoiceInfoTable.purchTable().TableId
        && specTrans.SpecRecId == vendInvoiceInfoTable.purchTable().RecId
        && specTrans.SpecCompany == vendInvoiceInfoTable.purchTable().DataAreaId;
    // Check the existence of marked open transacation in PO which will be used to settle the PO invoice.
    if (specTrans.Recid != 0)
    {
        throw warning("@AccountsPayable:ApplyPrepayBlockedByOpenTransSettle");
    }
    else
    {
        super();

        VendTrans vendTransLoc;
        VendInvoiceJour vendInvoiceJourLoc;

        select firstonly RecId from vendTransLoc
        where vendTransLoc.SettleAmountCur != 0
           && vendTransLoc.AmountCur != vendTransLoc.SettleAmountCur
        join vendInvoiceJourLoc
            where vendInvoiceJourLoc.OrderAccount == vendTransLoc.AccountNum
               && vendInvoiceJourLoc.InvoiceId == vendTransLoc.Invoice
               && vendInvoiceJourLoc.LedgerVoucher == vendTransLoc.Voucher
               && vendInvoiceJourLoc.InvoiceType == PurchInvoiceType::VendorAdvance
               && vendInvoiceJourLoc.PurchId == purchTable.PurchId;

        if (vendTransLoc.RecId)
        {
            warning("@AccountsPayable:VendInvoicePrePaymentExists");
        }

    }
}

