void clicked()
{
    Args args = new Args();
    MenuFunction menuFunction;

    if (vendInvoiceInfoTable.RecId == 0.0)
    {
        throw error("@SYS136802");
    }

    if (!vendInvoiceInfoTable.checkMarkupTrans())
    {
        return;
    }

    // calculate taxes - if TaxUncommitted record already exist then this will do nothing
    element.calculateTaxes();

    args.caller(element);
    args.record(vendInvoiceInfoTable);

    menuFunction = new MenuFunction(menuitemDisplayStr(SubledgerJournalAccountEntries), MenuItemType::Display);
    menuFunction.run(args);
}

public void clicked()
{
    if (vendInvoiceInfoTable)
    {
        // Submit to source document processor for an on-demand GL budget check
        SourceDocumentProcessorFacade::submitOnDemandSourceDocumentImpl(vendInvoiceInfoTable);

        if (PublicSectorUtils::isBudgetReservationEnabled())
        {
            VendInvoiceInfoTable::checkBudgetReservationBalance_PSN(
                vendInvoiceInfoTable.TableRefId,
                vendInvoiceInfoTable.SourceDocumentHeader);
        }

        vendInvoiceInfoTable_ds.updateBudgetCheckResult(false);
    }
}

/// <summary>
/// Responds when the <c>buttonPageParmTableMiscCharges</c> form button is clicked.
/// </summary>
void clicked()
{
    if (vendInvoiceInfoSubTable_ds.numberOfRowsLoaded() > 1) // more than one invoice, so edit the summary invoice
    {
        element.editMarkupTrans(this.menufunction(), vendInvoiceInfoTable);
    }
    else // only one invoice, so edit it
    {
        if (!vendInvoiceInfoSubTable)
        {
            vendInvoiceInfoSubTable.data(VendInvoiceInfoSubTable::find(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId, vendInvoiceInfoTable.PurchId));
        }
        element.editMarkupTrans(buttonSubTableMiscCharges.menufunction(), vendInvoiceInfoSubTable);
    }
}

/// <summary>
/// Responds when the <c>buttonPageParmTableAllocate</c> form button is clicked.
/// </summary>
void clicked()
{
    if (buttonSubTableMiscCharges.enabled()) // more than one invoice, so edit the summary invoice
    {
        element.editMarkupTrans(this.menufunction(), VendInvoiceInfoTable::find(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId));
    }
    else // only one invoice, so edit it
    {
        buttonSubTableAllocate.clicked();
    }

    if (LedgerParameters::isMiscellaneouscharges_IN())
    {
        VendInvoiceInfoLine_W_ds.reread();
        VendInvoiceInfoLine_W_ds.refresh();
    }

    element.refresh();
}

