{
    boolean reArrangeOk = true;

    if (reArrangeNow && purchParmUpdate.numberOfTables() > 1)
    {
        if (Box::yesNo("@SYS119223", DialogButton::Yes))
        {
            reArrangeOk = element.reArrange(false);
