    if (reArrangeOk)
    {
        PurchTotals_ParmTrans::showTax(menuitemDisplayStr(TaxTmpWorkVendInvoice), vendInvoiceInfoTable, element.specQty(), element);
    }

    if (isTaxServiceForPurchaseEnabled)
    {
        element.rereadAfterCalculatingTaxUsingTaxIntegration();
    }
}

