public void tabChanged(int _fromTab, int _toTab)
{
    super(_fromTab, _toTab);

    if (GridPanel.isActivePage())
    {
        // Set form to multi-invoice mode
        element.setFormDesign(VendInvoiceDesignMode::MultipleInvoice);
    }
    else
    {
        // Set form to invoice detail mode
        if (isSingleInvoiceHeader)
        {
            element.setFormDesign(VendInvoiceDesignMode::SingleInvoiceHeader);
        }
        else
        {
            element.setFormDesign(VendInvoiceDesignMode::MultipleInvoiceHeader);
        }
    }
}

public void jumpRef()
{
    Args args = new Args();

    args.lookupField(fieldNum(LtInvoiceAutoNumberingTable, NumberingCode));
    args.lookupValue(this.text());

    new MenuFunction(menuitemDisplayStr(LtInvoiceAutonumberingTable), MenuItemType::Display).run(args);
}

public void lookup()
{
    switch (documentStatus)
    {
        case DocumentStatus::PackingSlip:
            LtInvoiceAutoNumberingTable::lookup(this, DocNumberingModule_W::Purch, LtInvoiceAutoNumberingType::PackingSlip);
            break;
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            LtInvoiceAutoNumberingTable::lookup(this, DocNumberingModule_W::Purch, LtInvoiceAutoNumberingType::Invoice);
            break;
        default:
            super();
    }
}

public boolean validate()
{
    boolean ret = super();
    LtInvoiceAutoNumberingType invoiceAutoNumberingType;

    if (ret)
    {
        if (! this.text())
        {
            ret = checkFailed(strFmt("@SYS26332", fieldPName(LtInvoiceAutoNumberingTable, NumberingCode)));
        }
        if (ret)
        {
            switch (documentStatus)
            {
                case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
                    invoiceAutoNumberingType = LtInvoiceAutoNumberingType::Invoice;
                    break;
                case DocumentStatus::PackingSlip:
                    invoiceAutoNumberingType = LtInvoiceAutoNumberingType::PackingSlip;
                    break;
            }
            if (! LtInvoiceAutoNumberingTable::existUserNumbering(this.text(),
                                                                  DocNumberingModule_W::Purch,
                                                                  invoiceAutoNumberingType))
            {
                ret = checkFailed(strFmt("@GLS110719", this.text(), curUserId()));
            }
        }
    }
    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        DataAreaId newDataAreaId = this.valueStr();

        if (newDataAreaId != curExt())
        {
            element.createNewSessionForNewCompany(newDataAreaId);
        }
    }
    return ret;
}

public void lookup()
{
    SysTableLookup lookup = SysTableLookup::newParameters(tableNum(MyLegalEntities), this, false);
    lookup.addLookupField(fieldNum(MyLegalEntities, DataArea));

    Query newQuery = new Query();
    newQuery.allowCrossCompany(true);

    QueryBuildDataSource ds = newQuery.addDataSource(tableNum(MyLegalEntities));

    lookup.parmQuery(newQuery);
    lookup.performFormLookup();
}

