/// <summary>
/// Opens PurchSelectLines form.
/// </summary>
/// <remarks>
/// Opens the PurchSelectLines, passing in the current parmId value.
/// The form is opened modally so no changes can be made to the PurchEditLines document during
/// the PurchSelectLines process.
/// </remarks>
void clicked()
{
    // Do not allow adding a purchase order when account is intercompany and a non-PO line exists.
    if (vendInvoiceInfoTable.existIntercompanyNonPurchaseOrderLine())
    {
        error("@AccountsPayable:VendorInvoiceIntercompanyPurchaseOrderValidation");
        return;
    }

    FormRun purchSelectLinesFormRun;
    Args args = new Args();

    super();

    args.name(formStr(PurchSelectLines));
    args.record(vendInvoiceInfoTable);
    args.parmObject(purchFormLetter);
    args.caller(element);

    purchSelectLinesFormRun = classfactory.formRunClass(args);

    purchSelectLinesFormRun.init();
    purchSelectLinesFormRun.run();

    // By putting 'true' in the wait statement, the user is prevented from returning to the calling form.
    purchSelectLinesFormRun.wait(true);

    element.refresh();
    element.checkForPrepaymentApplicationFlag();
    element.checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags::SalesTaxAdjustmentsProrated, "@AccountsPayable:SalesTaxAdjustmentsProrated");
}

public void jumpRef()
{
    Args args = new Args();

    MenuFunction mf = new MenuFunction(menuItemDisplayStr(VendPackingSlipJournal), MenuItemType::Display);
    FormRun formRun = mf.create(args);

    QueryBuildDataSource qBd;
    FormDataSource formDataSource;
    QueryBuildRange qBr;

    formDataSource = formRun.dataSource();

    switch (documentStatus)
    {
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            formDataSource.query(VendPackingSlipJour::queryPackingSlipIdForUpdate_W(vendInvoiceInfoTable.ParmId,
                                                                                    vendInvoiceInfoTable.PurchId,
                                                                                    vendInvoiceInfoTable.TableRefId));
            break;
        case DocumentStatus::PackingSlip:
            formDataSource.query(VendPackingSlipJour::queryPackingSlipStorno_LT());
            break;
    }
    if (vendInvoiceInfoTable_W.PackingSlipIdForUpdate_W)
    {
        qBd = formDataSource.query().dataSourceTable(tableNum(VendPackingSlipJour));
        qBr = qBd.addRange(fieldNum(VendPackingSlipJour, PackingSlipId));
        qBr.value(vendInvoiceInfoTable_W.PackingSlipIdForUpdate_W);
    }
    formRun.run();
    formRun.detach();
}

public void lookup()
{
    switch (documentStatus)
    {
        case DocumentStatus::Invoice, DocumentStatus::Facture_RU:
            VendPackingSlipJour::lookupPackingSlipId_W(this, VendPackingSlipJour::queryPackingSlipIdForUpdate_W(vendInvoiceInfoTable.ParmId,
                                                                                                                vendInvoiceInfoTable.PurchId,
                                                                                                                vendInvoiceInfoTable.TableRefId));
            break;
        case DocumentStatus::PackingSlip:
            VendPackingSlipJour::lookupPackingSlipId_W(this,VendPackingSlipJour::queryPackingSlipStorno_LT());
            break;
    }
}

public boolean leave()
{
    boolean ret = super();

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendInvoiceSaveStatus)).allowEdit(vendInvoiceInfoTable.VendInvoiceSaveStatus != VendInvoiceSaveStatus::Pending);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendInvoiceSaveStatus)).skip(vendInvoiceInfoTable.VendInvoiceSaveStatus == VendInvoiceSaveStatus::Pending);

    return ret;
}

public boolean modified()
{
    boolean ret;
    boolean isPrepaymentSkippedByUser;

    if (!VendInvoiceAutomationStateManagement::isIncludedInAutomatedProcessing(vendInvoiceInfoTable))
    {
        boolean automaticPrepaymentEnabled = element.isAutomaticPrepaymentEnabled();
        if (automaticPrepaymentEnabled)
        {
            VendAdvanceInvoiceApplication vendAdvanceInvoiceApplication = VendAdvanceInvoiceApplication::construct(vendInvoiceInfoTable);
            boolean isPrepaymentPending = vendAdvanceInvoiceApplication.hasPrepaymentApplicationPending(true);

            if (isPrepaymentPending && ! VendInvoiceInfoTable.hasPrepaymentApplicationLines())
            {
                DialogButton result = Box::yesNo("@AccountsPayable:AutomationSkipPrepaymentApplicationConfirmation",DialogButton::No, "@AccountsPayable:AutomationPrepaymentApplicationTitle");
                if (result == DialogButton::Yes)
                {
                    isPrepaymentSkippedByUser = true;
                }
                else
                {
                    return ret;
                }
            }
        }

        DialogButton result = Box::yesNo("@AccountsPayable:IncludeInProcessAutomationConfirmationMessage", DialogButton::No, "@AccountsPayable:VendInvoiceIncludeInAutomatedProcessing");
        if (result == DialogButton::Yes)
        {
            VendInvoiceAutomationStateManagement::setInvoiceAutomationToIncluded(vendInvoiceInfoTable);

            DocumentEventLogRequest logRequest = new DocumentEventLogRequest();

            if (VendInvoiceIncludeManualCreatedInvoiceIntoAutomationFeature::isEnabled()
                && vendInvoiceInfoTable.DocumentOrigin != DocumentOrigin::Service
                && !(DocumentEventLog::findMostRecentLogEntryOfType(tableNum(VendInvoiceInfoTable),
                        vendInvoiceInfoTable.RecId,
                        enumNum(VendInvoiceAutomationManualInvoiceIncludedInAutomationByUser))))
            {
                logRequest.eventEnumId = enumNum(VendInvoiceAutomationManualInvoiceIncludedInAutomationByUser);
                logRequest.eventEnumValue = VendInvoiceAutomationManualInvoiceIncludedInAutomationByUser::NotSpecified;
                logRequest.sourceTableId = tableNum(VendInvoiceInfoTable);
                logRequest.sourceRecId = vendInvoiceInfoTable.RecId;
                logRequest.parameters = [curUserId()];
                DocumentEventLog::logDocumentEvent(logRequest);
            }
            else
            {
                logRequest.eventEnumId = enumNum(VendInvoiceAutomationResumedByUser);
                logRequest.eventEnumValue = VendInvoiceAutomationResumedByUser::NotSpecified;
                logRequest.sourceTableId = tableNum(VendInvoiceInfoTable);
                logRequest.sourceRecId = vendInvoiceInfoTable.RecId;
                logRequest.parameters = [curUserId()];
                DocumentEventLog::logDocumentEvent(logRequest);
            }

            if (isPrepaymentSkippedByUser)
            {
                DocumentEventLogRequest prepaymentLogRequest = new DocumentEventLogRequest();
                prepaymentLogRequest.eventEnumId = enumNum(VendInvoiceAutomationPrepaymentApplicationEvent);
                prepaymentLogRequest.eventEnumValue = VendInvoiceAutomationPrepaymentApplicationEvent::SkippedByUser;
                prepaymentLogRequest.sourceTableId = tableNum(VendInvoiceInfoTable);
                prepaymentLogRequest.sourceRecId = vendInvoiceInfoTable.RecId;
                prepaymentLogRequest.parameters = [curUserId()];
                DocumentEventLog::logDocumentEvent(prepaymentLogRequest);
            }
            else if (automaticPrepaymentEnabled)
            {
                // if history has prepaymentevent latest status as Failed, insert manually adjusted event value
                DocumentEventLog prepaymentEventLog = DocumentEventLog::findMostRecentLogEntryOfType(tableNum(VendInvoiceInfoTable), vendInvoiceInfoTable.RecId, enumNum(VendInvoiceAutomationPrepaymentApplicationEvent));

                if (!prepaymentEventLog
                    || (prepaymentEventLog
                        && (prepaymentEventLog.EventEnumValue == VendInvoiceAutomationPrepaymentApplicationEvent::Failed)
                            || (prepaymentEventLog.EventEnumValue == VendInvoiceAutomationPrepaymentApplicationEvent::SkippedByUser)))
                {
                    DocumentEventLogRequest prepaymentLogRequest = new DocumentEventLogRequest();
                    prepaymentLogRequest.eventEnumId = enumNum(VendInvoiceAutomationPrepaymentApplicationEvent);
                    prepaymentLogRequest.eventEnumValue = VendInvoiceAutomationPrepaymentApplicationEvent::ManuallyEdited;
                    prepaymentLogRequest.sourceTableId = tableNum(VendInvoiceInfoTable);
                    prepaymentLogRequest.sourceRecId = vendInvoiceInfoTable.RecId;
                    prepaymentLogRequest.parameters = [curUserId()];
                    DocumentEventLog::logDocumentEvent(prepaymentLogRequest);
                }
            }

            ret = super();
            vendInvoiceInfoTable_ds.research(true);
        }
    }
    else
    {
        DialogButton result = Box::yesNo("@AccountsPayable:ExcludeFromProcessAutomationConfirmationMessage", DialogButton::No);
        if (result == DialogButton::Yes)
        {
            VendInvoiceAutomationStateManagement::setInvoiceAutomationToPaused(vendInvoiceInfoTable);

            DocumentEventLogRequest logRequest = new DocumentEventLogRequest();
            logRequest.eventEnumId = enumNum(VendInvoiceAutomationPausedByUserEvent);
            logRequest.eventEnumValue = VendInvoiceAutomationPausedByUserEvent::NotSpecified;
            logRequest.sourceTableId = tableNum(VendInvoiceInfoTable);
            logRequest.sourceRecId = vendInvoiceInfoTable.RecId;
            logRequest.parameters = [curUserId()];
            DocumentEventLog::logDocumentEvent(logRequest);

            ret = super();
            vendInvoiceInfoTable_ds.research(true);
        }
    }
    if (VendInvoiceIncludeManualCreatedInvoiceIntoAutomationFeature::isEnabled())
    {
        VendInvoiceAutomationHistoryTimelineDialog.visible(vendInvoiceInfoTable.AutomationStatus != VendInvoiceAutomationStatus::NotApplicable
            && (VendParameters::findByCompany(vendInvoiceInfoTable.DataAreaId).AutomationWorkflowSubmission
                || VendParameters::findByCompany(vendInvoiceInfoTable.DataAreaId).AutomationProductReceiptMatching));
    }
    return ret;
}

/// <summary>
/// Click to view vendor invoice automation matching recent results.
/// </summary>
public void clicked()
{
    super();

    DocumentEventLog mostRecentMatchLogEntry = DocumentEventLog::findMostRecentLogEntryOfType(tableNum(VendInvoiceInfoTable), VendInvoiceInfoTable.RecId, enumNum(VendInvoiceMatchProductReceiptToInvoiceLineEvent));

    // Check if the recent match log entry is written by manual user action
    if (mostRecentMatchLogEntry.ExecutionId == emptyGuid())
    {
        info("@AccountsPayable:AutomationMatchingManualUserActionImpactInfo");
    }
    else
    {
        VendInvoiceAutomationExecutionResultsOpener::openMessageLogForMostRecentInvoiceDocumentEventOfType(
            VendInvoiceInfoTable,
            enumNum(VendInvoiceMatchProductReceiptToInvoiceLineEvent),
            VendInvoiceAutomationConstants::AutomationMatchingTypeName);
    }
}

/// <summary>
/// Opens the InvoiceMatchingDetails form passing along the current user filter defined on the lines tab of this form.
/// </summary>
public void clicked()
{
    MenuFunction menuFunction;
    Args args = new Args();

    element.updateExpectedMatchValuesAndRefresh();

    super();

    args.caller(element);
    args.record(vendInvoiceInfoLine);
