public void clicked()
{
    if (vendInvoiceInfoLine)
    {
        // Submit to source document processor for an on-demand GL budget check
        SourceDocumentProcessorFacade::submitOnDemandSourceDocLineImpl(vendInvoiceInfoLine);

        if (vendInvoiceInfoLine.isBudgetReservationLineReferenced())
        {
            // Check if the referenced budget reservation line has available balance.
            BudgetReservation_PSN::checkBudgetReservationBalance(vendInvoiceInfoLine.BudgetReservationLine_PSN, vendInvoiceInfoTable.SourceDocumentHeader);
        }

        vendInvoiceInfoLine_ds.updateBudgetCheckResult();
    }
}

