public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (vendInvoiceInfoLine.BudgetReservationLine_PSN)
    {
        vendInvoiceInfoLine_ds.setLineFields();
    }

    return isModified;
}

public void clicked()
{
    BudgetReservationLineSelectorContract_PSN budgetResLineSelectorContract;
    budgetResLineSelectorContract = BudgetReservationLineSelectorContract_PSN::newFromParameters(
        BudgetReservationRelievingDocument_PSN::VendorInvoice,
        vendInvoiceInfoTable.TransDate);

    budgetResLineSelectorContract.parmDoShowEditableSelectorForm(
        vendInvoiceInfoLine_ds.numberOfRowsLoaded() > 0
        && vendInvoiceInfoLine_ds.object(fieldNum(vendInvoiceInfoLine, BudgetReservationLine_PSN)).allowEdit());

    BudgetReservationLineSelectorForm_PSN::showBudgetResLineSelector(
        budgetResLineSelectorContract,
        vendInvoiceInfoLine,
        element);

    BudgetReservationLineRecId_PSN budgetReservationLineRecId;
    budgetReservationLineRecId = budgetResLineSelectorContract.getSingleBudgetReservationLineRecId();

    if (budgetReservationLineRecId)
    {
        vendInvoiceInfoLine.BudgetReservationLine_PSN = budgetReservationLineRecId;

        if (vendInvoiceInfoLine.validateField(fieldNum(VendInvoiceInfoLine, BudgetReservationLine_PSN)))
        {
            vendInvoiceInfoLine.modifiedField(fieldNum(VendInvoiceInfoLine, BudgetReservationLine_PSN));
        }
        else
        {
            vendInvoiceInfoLine.BudgetReservationLine_PSN = vendInvoiceInfoLine.orig().BudgetReservationLine_PSN;
        }
    }
}

