/// <summary>
/// Constructs a lookup for the reason code field.
/// </summary>
/// <remarks>
/// The lookup shows the appropriate reason codes (if any) given the offset and main account selections.
/// </remarks>
public void lookup()
{
    //Calls the lookup method passing Vendor as both main and offset account types; boolean values of true
    //and false are passed for them respectively, since the offset account is not present in this journal.
    ReasonFormTable::reasonCodeLookup(this, LedgerJournalACType::Vend, LedgerJournalACType::Vend, true, false);
}

/// <summary>
/// Validate existence of vendor type reason code in ReasonTable.
/// </summary>
/// <returns>
/// A boolean indicating whether the reason code was valid for vendor type reason or not.
/// </returns>
public boolean validate()
{
    boolean ret = super();

    ret = ReasonTable::validateReasonCode(this.text(), LedgerJournalACType::Vend, true, LedgerJournalACType::Vend);

    return ret;
}

public void lookup()
{
    CustVendInvoiceTrans custVendInvoiceTrans;

    custVendInvoiceTrans = CustVendInvoiceTrans::lookupCorrectedTrans_RU(this,
                                                                         vendInvoiceInfoTable.vendInvoiceInfoTable_RU().CorrectedInvoiceId_RU,
                                                                         vendInvoiceInfoTable.vendInvoiceInfoTable_RU().CorrectedInvoiceDate_RU,
                                                                         vendInvoiceInfoTable.InvoiceAccount,
                                                                         vendInvoiceInfoTable.CurrencyCode,
                                                                         vendInvoiceInfoLine.purchLine());

    if (custVendInvoiceTrans.RecId)
    {
        vendInvoiceInfoLineCorrLink_RU.CorrTransRecId = custVendInvoiceTrans.RecId;

        if (vendInvoiceInfoLineCorrLink_RU_DS.validateWrite())
        {
            vendInvoiceInfoLineCorrLink_RU_DS.write();
            vendInvoiceInfoLineCorrLink_RU_DS.active();
        }
    }
}

