public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        if (this.value())
        {
            if (vendInvoiceInfoLine.purchLine().creditNoteLine())
            {
                vendInvoiceInfoLine.ReceiveNow--;
            }
            else
            {
                vendInvoiceInfoLine.ReceiveNow++;
            }
        }
        else
        {
            if (vendInvoiceInfoLine.purchLine().creditNoteLine())
            {
                vendInvoiceInfoLine.ReceiveNow++;
            }
            else
            {
                vendInvoiceInfoLine.ReceiveNow--;
            }
        }
        vendInvoiceInfoLine.modifiedReceiveNow();
        vendInvoiceInfoLine_ds.refresh();
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    vendInvoiceGroupDefaultGroup = invoiceGrouping_VendInvoiceGroup.text();

    return ret;
}

