void clicked()
{
    VendPaymSched vendPaymSched;

    DialogButton reArrange = DialogButton::No;
    VendInvoiceInfoTable localVendInvoiceInfoTable;

    if (reArrangeNow)
    {
        reArrange = Box::yesNo("@AccountsPayable:PaymScheduleRearrangeVendEditInvoice", DialogButton::Yes);

        if (reArrange == DialogButton::Yes)
        {
            while select RecId, TableId from localVendInvoiceInfoTable
                where localVendInvoiceInfoTable.ParmId == vendInvoiceInfoTable.ParmId
            {
                if (VendPaymSched::exist(localVendInvoiceInfoTable.TableId, localVendInvoiceInfoTable.RecId))
                {
                    ttsBegin;
                    VendPaymSched::deleteVendPaymSched(localVendInvoiceInfoTable.TableId, localVendInvoiceInfoTable.RecId);
                    ttsCommit;
                }
            }

            if (!element.reArrange(false))
            {
                return;
            }
        }
    }

    if (!VendPaymSched::exist(vendInvoiceInfoTable.TableId, vendInvoiceInfoTable.RecId))
    {
        CustVendPaymSched custVendPaymSched = CustVendPaymSched::construct(SysModule::Vend, vendInvoiceInfoTable);

        if (!vendInvoiceInfoTable.isNonPO())
        {
            vendPaymSched = vendPaymSched::find(vendInvoiceInfoTable.purchTable().TableId, vendInvoiceInfoTable.purchTable().RecId);
        }

        if (reArrange == DialogButton::No && vendPaymSched && vendPaymSched.CopyPreciselyToInvoice)
        {
            custVendPaymSched.copyVendPaymSched(VendPaymSched::find(purchTable.TableId, purchTable.RecId));
        }
        else
        {
            PaymSchedId paymentSched = purchTable.recId ? purchTable.PaymentSched : vendInvoiceInfoTable.PaymentSched;
            custVendPaymSched.copyPaymSched(PaymSched::find(paymentSched));
            vendInvoiceInfoTable.createPaymentSched();
        }
    }

    super();
}

