public boolean modified()
{
    boolean ret = super();

    vendInvoiceInfoTable_RU.Correct_RU = vendInvoiceInfoTableCorrectionType_RU.CorrectionType != FactureCorrectionType_RU::Unknown;
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, Correct_RU)).modified();
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, vatOnPayment_RU)).allowEdit(vendInvoiceInfoTableCorrectionType_RU.CorrectionType != FactureCorrectionType_RU::Revision);

    vendInvoiceInfoTable_ds.initCorrFactureDisplay_RU();

    if (vendInvoiceInfoTableCorrectionType_RU.CorrectionType == FactureCorrectionType_RU::Unknown)
    {
        VendInvoiceInfoLineCorrLink_RU::clearLinksByVendInvoiceInfoTable(vendInvoiceInfoTable);
    }

    return ret;
}

