Method.Source:
[Form]
public class VendEditInvoiceHeaderStaging extends FormRun
{
    FormRun caller;
    QueryBuildRange qrRecId;
    VendorInvoiceHeaderStaging argsRecord;

}

/// <summary>
/// Initialises the form
/// </summary>
public void init()
{
    #SysSystemDefinedButtons

    if (!element.args().caller())
    {
        throw error(strFmt("@SYS84645", element.name()));
    }
    caller = element.args().caller();

    if (element.args().record() && element.args().record().TableId == tableNum(VendorInvoiceHeaderStaging))
    {
        argsRecord = element.args().record();
    }

    super();

    // Hide Attach button
    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (attachButton)
    {
        attachButton.visible(false);
    }
}

/// <summary>
/// Fetch the attachment records for the header staging records
/// </summary>
/// <returns><c>VendorInvoiceDocumentAttachmentStaging</c> records related to selected header staging record</returns>
public VendorInvoiceDocumentAttachmentStaging findAttachmentRecords()
{
    VendorInvoiceDocumentAttachmentStaging attachmentRecords;

    select attachmentRecords
        where attachmentRecords.DefinitionGroup == vendorInvoiceHeaderStaging.DefinitionGroup
            && attachmentRecords.ExecutionId == vendorInvoiceHeaderStaging.ExecutionId
            && attachmentRecords.HeaderReference == vendorInvoiceHeaderStaging.HeaderReference;

    return attachmentRecords;
}

/// <summary>
/// Filter passed record
/// </summary>
public void executeQuery()
{
    qrRecId.value(queryValue(argsRecord.RecId));
    super();
}

/// <summary>
/// Initiate the query range
/// </summary>
public void init()
{
    super();
    qrRecId = this.query().dataSourceNo(1).addRange(fieldNum(VendorInvoiceHeaderStaging, RecId));
}

/// <summary>
///  Show or hide the button when record is selected
/// </summary>
/// <returns>Returns the integer value of the super() call</returns>
public int active()
{
    int ret = super();

    VendorInvoiceDocumentAttachmentStaging stagingAttachmentTable = element.findAttachmentRecords();
    ViewHideAttachment.visible(stagingAttachmentTable.RecId != 0);
    return ret;
}

/// <summary>
/// Refresh caller form after execution
/// </summary>
public void clicked()
{
    super();
    caller.dataSource().research(true);
}

/// <summary>
/// The button click will toggle the button text and image control
/// </summary>
public void clicked()
{
    super();
    ImageGroupControl.visible(!ImageGroupControl.visible());
    if (ImageGroupControl.visible())
    {
        ViewHideAttachment.text("@AccountsPayable:HideAttachment");
    }
    else
    {
        ViewHideAttachment.text("@AccountsPayable:ViewAttachment");
    }
}
