void allowEditsAllDatasources(boolean _allowEdits)
{
    int i;
    FormDataSource dataSource;

    for (i = 1; i <= element.dataSourceCount(); i++)
    {
        dataSource = element.dataSource(i);

        // Only allow edit if initial property allowed it
        if (_allowEdits && !uneditableDataSourcesSet.in(i))
        {
            dataSource.allowEdit(true);
        }
        else
        {
            dataSource.allowEdit(false);
        }
    }
}

void allowEditsForHeader()
{
    workflowCompleted = false;

    if (isInAutomatedProcessing)
    {
        element.design().viewEditMode(ViewEditMode::View);
        workflowFullEnabled = false;
        headerDeleteEnabled = false;
        workflowPartiallyEnabled = false;

        element.allowEditsAllDatasources(false);
        ShowHeaderAttachments.enabled(false);
    }
    else
    {
        if (vendInvoiceFormState.invoiceHeaderEditFull(vendInvoiceInfoTable))
        {
            element.allowEditsAllDatasources(true);
            workflowFullEnabled = true;
            workflowPartiallyEnabled = true;
            headerDeleteEnabled = true;
        }
        else if (vendInvoiceFormState.invoiceHeaderEditPartial(vendInvoiceInfoTable))
        {
            element.allowEditsAllDatasources(true);
            workflowFullEnabled = false;
            headerDeleteEnabled = false;
            workflowPartiallyEnabled = true;
        }
        else
        {
            element.allowEditsAllDatasources(false);
            workflowFullEnabled = false;
            workflowPartiallyEnabled = VendInvoiceWorkflow::isUserTaskOrApprovalOwner(vendInvoiceInfoTable);
            headerDeleteEnabled = (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved);
            workflowCompleted = (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved);
        }
    }

    element.enableHeaderActions();
}

void allowEditsForLine()
{
    if (isInAutomatedProcessing)
    {
        element.allowLineEdits(false);
        element.enableLineActions(false);
    }
    else
    {

        if (vendInvoiceFormState.invoiceLineEditFull(vendInvoiceInfoTable))
        {
            element.allowLineEdits(true);
            element.enableLineActions(true);
        }
        else if (vendInvoiceFormState.invoiceLineEditPartial(vendInvoiceInfoTable))
        {
            element.allowLineEdits(true);
            element.enableLineActions(false);
        }
        else
        {
            element.allowLineEdits(false);
            element.enableLineActions(false);
        }
    }
}

public void allowLineEdits(boolean _canEdit)
{
    vendInvoiceInfoLine_ds.allowEdit(_canEdit);
}

public void close()
{
    super();

    if (vendInvoiceInfoTable)
    {
        element.saveInvoice();
    }

    PurchParmUpdate::deleteWithoutUpdate(purchParmUpdate.ParmId);

    // Refresh the calling form
    VendIInvoiceRefresh vendIInvoiceRefresh = element.args().caller() as VendIInvoiceRefresh;
    if (vendIInvoiceRefresh)
    {
        vendIInvoiceRefresh.refresh();
    }
}

