public void enableHeaderActions()
{
    vendInvoiceInfoTable_ds.allowDelete(headerDeleteEnabled);

    // Disable the Invoice line's Delete functionality through keyboard
    vendInvoiceInfoLine_ds.allowDelete(workflowFullEnabled);

    buttonPageParmTableMiscCharges.enabled(workflowPartiallyEnabled);

    // Disable the controls
    removeButton.enabled(workflowFullEnabled);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PurchId)).enabled(workflowFullEnabled);

    taxTransSource.enabled(!workflowCompleted && workflowPartiallyEnabled);
}

void enableLineActions(boolean _enable)
{
    vendInvoiceInfoLine_ds.allowDelete(_enable);
    vendInvoiceInfoLine_ds.allowCreate(_enable);

    removeButton.enabled(_enable);
    ButtonView.enabled(_enable);
    ShowLineAttachments.enabled(_enable);
}

public void init()
{
    #SysSystemDefinedButtons

    if (!element.args() || !element.args().record() || !element.args().caller())
    {
        error("@SYS22539");
        this.close();
    }

    VendInvoiceInfoTable localVendInvoiceInfoTable = VendInvoiceInfoTable::findRecId(element.args().record().RecId);

    if (!localVendInvoiceInfoTable)
    {
        error("@SYS112451");
        this.close();
    }

    if (localVendInvoiceInfoTable.ParmJobStatus == ParmJobStatus::Executed)
    {
        error("@AccountsPayable:PostedInvoiceCannotBeEdited");
        this.close();
    }

    super();

    // Hide attach button
    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (attachButton)
    {
        attachButton.visible(false);
    }

    if (!localVendInvoiceInfoTable.ParmId)
    {
        // A saved invoice was passed to the form
        PurchFormLetter_Invoice purchFormletter = PurchFormLetter_Invoice::newFromSavedInvoice(localVendInvoiceInfoTable);

        purchFormLetter.initNewPurchParmUpdate();
        purchParmUpdate = purchFormLetter.purchParmUpdate();
        purchFormLetter.chooseLines(true, localVendInvoiceInfoTable);

        if (purchFormLetter.isSetActiveInvoiceFlag(VendActiveInvoiceFlags::InvoiceInUse))
        {
            error("@AccountsPayable:InvoiceInUse");
            this.close();
        }
    }
    else if (!element.args().parm())
    {
        // An existing active invoice was passed to the form - warn the user that it cannot be edited
        error("@AccountsPayable:InvoiceInUse");
        this.close();
    }
    else
    {
        // A newly-created active invoice was passed to the form from the vendor portal create invoice process
        purchParmUpdate = localVendInvoiceInfoTable.purchParmUpdate();
    }

    isInAutomatedProcessing = VendInvoiceAutomationStateManagement::isInvoiceReadOnly(localVendInvoiceInfoTable);

    vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, ParmId)).value(queryValue(purchParmUpdate.ParmId));
    vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).clearDynalinks();

    vendInvoiceFormState = PurchEditLinesForm::construct(DocumentStatus::Invoice);

    previousNonPO = true;

    // Cache each of the form datasources' initial allowEdit property
    uneditableDataSourcesSet = new Set(Types::Integer);
    FormDataSource dataSource;

    for (int i = 1; i <= element.dataSourceCount(); i++)
    {
        dataSource = element.dataSource(i);

        if (!dataSource.allowEdit())
        {
            uneditableDataSourcesSet.add(i);
        }
    }
}

