private Map createFiltersForExcelVendorInvoiceEntity(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    str lineEntityName = tableStr(VendorInvoiceLineEntity);
    str lineEntityTableRefId = fieldStr(VendorInvoiceLineEntity, HeaderReference);
    str lineEntityDataAreaId = fieldStr(VendorInvoiceLineEntity, dataAreaId);
    str headerEntityName = tableStr(VendorInvoiceHeaderEntity);
    str headerEntityTableRefId = fieldStr(VendorInvoiceHeaderEntity, HeaderReference);
    str headerEntityDataAreaId = fieldStr(VendorInvoiceHeaderEntity, dataAreaId);

    Map filtersToApply = new Map(Types::String, Types::Class);

    // Create lines filter
    ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);

    var filter = filterBuilder.and(
        filterBuilder.areEqual(lineEntityTableRefId, _vendInvoiceInfoTable.TableRefId),
        filterBuilder.areEqual(lineEntityDataAreaId, _vendInvoiceInfoTable.DataAreaId));

    filtersToApply.insert(lineEntityName, filter);

    // Create header filter
    ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);

    filter = filterBuilder.and(
        lineFilterBuilder.areEqual(headerEntityTableRefId, _vendInvoiceInfoTable.TableRefId),
        lineFilterBuilder.areEqual(headerEntityDataAreaId, _vendInvoiceInfoTable.DataAreaId));

    filtersToApply.insert(headerEntityName, filter);

    return filtersToApply;
}

public int active()
{
    int ret = super();

    invoiceId.text(vendInvoiceInfoTable.Num);

    element.updateWorkflowIconForHeader();

    if (vendInvoiceFormState.initInvoiceHeaderState(vendInvoiceInfoTable))
    {
        element.allowEditsForHeader();
        element.allowEditsForLine();
    }

    if (!vendInvoiceInfoTable.isNonPO())
    {
        if (!purchTable)
        {
            purchTable.data(vendInvoiceInfoTable.purchTable());
        }

        vendInvoiceFormState.setPurchTable(purchTable);
    }

    element.setHeaderFields();

    return ret;
}

/// <summary>
/// Checks for data specific situations when a field value in the <c>VendInvoiceInfoTable</c> Form Data Source changes.
/// </summary>
/// <param name="_fieldId">
/// The field identifier of the field being modified.
/// </param>
/// <param name="_unapprove">
/// true if the approveVariance should be called; otherwise false.
/// </param>
public void modifiedField(FieldId _fieldId, boolean _unapprove)
{
    NoYes varianceApprovedReset;
    boolean currencyExchChanged;

    if (_unapprove)
    {
        //  Unapprove the invoice, but don't save changes here in case distributions need to be updated
        vendInvoiceInfoTable.approveVariance(false, false);

        if (vendInvoiceInfoTable.VarianceApproved == NoYes::No)
        {
            // If the invoice's varianceApproved was reset, then the expected amounts were refreshed
            varianceApprovedReset = NoYes::Yes;
        }
    }

    switch (_fieldId)
    {
        case fieldNum(VendInvoiceInfoTable, DocumentDate):
            if (vendInvoiceInfoTable.useDocumentDate() == true)
            {
                currencyExchChanged = true;
            }
            break;

        case fieldNum(VendInvoiceInfoTable, Num):
            invoiceId.text(vendInvoiceInfoTable.Num);
            break;
    }

    if (varianceApprovedReset == NoYes::No && currencyExchChanged)
    {
        // If the invoice's varianceApproved was not reset, then the expected amounts need to be refreshed when fields affect the currency/exchange rate
        VendInvoiceInfoSubMarkupMatchingTrans::updateExpectedValues(vendInvoiceInfoTable);
        VendInvoiceMatching::updateExpectedValues(vendInvoiceInfoTable);
    }
}

public void delete()
{
    PurchTable emptyPurchTable = null;

    super();

    // If we now no longer have any invoices loaded, the vendInvoiceInfoTable buffer contains stale values, so we need to clear the buffer in
    // order to properly exit the form without saving
    if (vendInvoiceInfoTable_ds.numberOfRowsLoaded() == 0)
    {
        vendInvoiceInfoTable.clear();
    }

    element.setHeaderFields();
    element.updateWorkflowIconForHeader();

    purchTable.data(emptyPurchTable);
}

public void create(boolean _append = false)
{
    if (vendInvoiceInfoTable)
    {
        element.saveInvoice();
        vendInvoiceInfoTable_ds.research(true);
    }

    super(_append);

    element.updateWorkflowIconForHeader();

    element.allowEditsForHeader();
}

public void executeQuery()
{
    super();

    element.allowEditsForHeader();
}

public void write()
{
    VendInvoiceInfoTable originalVendInvoiceInfoTable = vendInvoiceInfoTable.orig();

    if (vendInvoiceInfoLine_ds.numberOfRowsLoaded() == 0
         && purchTable && originalVendInvoiceInfoTable.PurchId != vendInvoiceInfoTable.PurchId)
    {
        // If the invoice has no lines, try to default them from the PO
        PurchFormLetterInvoiceContract purchFormLetterInvoiceContract = PurchFormLetterInvoiceContract::construct();
        PurchTable localPurchTable = vendInvoiceInfoTable.purchTable();

        purchFormLetterInvoiceContract.parmSourceTable(localPurchTable);
        purchFormLetterInvoiceContract.parmCallerTable(localPurchTable);
        purchFormLetterInvoiceContract.parmSpecQty(PurchUpdate::All);
        purchFormLetterInvoiceContract.parmSpecQtyIsSet(NoYes::Yes);
        purchFormLetterInvoiceContract.parmParmTable(vendInvoiceInfoTable);

        purchFormLetterInvoiceContract.parmParmUpdate(purchParmUpdate);
        purchFormLetterInvoiceContract.parmParmId(purchParmUpdate.ParmId);
        boolean reuseParmUpdate = true;

        PurchFormletterParmData purchFormletterParmData = PurchFormletterParmData::newChooseLines(purchFormLetterInvoiceContract.pack(), reuseParmUpdate);
        purchFormletterParmData.createData();

        VendInvoiceInfoTable newVendInvoiceInfoTable = VendInvoiceInfoTable::findPurchId(vendInvoiceInfoTable.PurchId, purchFormletterParmData.parmParmId());

        // Delete the empty original invoice that was created upon initial save
        if (originalVendInvoiceInfoTable)
        {
            Docu::copy(originalVendInvoiceInfoTable, newVendInvoiceInfoTable);
            VendInvoiceInfoTable::deleteTableRefId(originalVendInvoiceInfoTable.ParmId, originalVendInvoiceInfoTable.TableRefId);
        }

        vendInvoiceInfoTable.data(newVendInvoiceInfoTable);
        vendInvoiceInfoTable_ds.setCurrent();
    }
    else
    {
        super();
    }

    element.updateWorkflowIconForHeader();
}

/// <summary>
/// A method to return whether the document has attachments.
/// </summary>
/// <returns>true if the document has attachments; otherwise, false.</returns>
public display boolean hasAttachments(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    boolean ret;

    headerAttachmentsObservableLink.observe();

    ret = DocuRef::findTableIdRecId(_vendInvoiceInfoTable.DataAreaId, _vendInvoiceInfoTable.TableId, _vendInvoiceInfoTable.RecId).RecId != 0;

    return ret || _vendInvoiceInfoTable.checkIfLinesHaveAttachments();
}

public void modified()
{
    super();

    vendInvoiceInfoTable.Ordering = DocumentStatus::Invoice;
    vendInvoiceInfoTable.DocumentOrigin = DocumentOrigin::VendorPortal;
    purchTable.data(vendInvoiceInfoTable.purchTable());
    purchTable_ds.refresh();

    vendInvoiceInfoTable.ParmId = purchParmUpdate.ParmId;
    vendInvoiceInfoTable.defaultRow(purchTable, null, true);

    // When the PO is entered on an invoice that is new or has no lines, force a save so the lines are created
    element.saveAndReloadNewOrNoLineInvoice();

    element.setHeaderFields();
}

public boolean validate()
{
    boolean ret = super();

    if (ret && vendInvoiceInfoTable.purchTable().isInterCompanyOrder())
    {
        ret = checkFailed(strFmt("@SYS129550", vendInvoiceInfoTable.PurchId, vendInvoiceInfoTable.Num));
    }

    return ret;
}

