public void clicked()
{
    if (vendInvoiceInfoSubTable_ds.numberOfRowsLoaded() > 1) // more than one invoice, so edit the summary invoice
    {
        element.editMarkupTrans(this.menufunction(), vendInvoiceInfoTable);
    }
    else // only one invoice, so edit it
    {
        if (!vendInvoiceInfoSubTable)
        {
            vendInvoiceInfoSubTable.data(VendInvoiceInfoSubTable::find(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId, vendInvoiceInfoTable.PurchId));
        }
        element.editMarkupTrans(buttonSubTableMiscCharges.menufunction(), vendInvoiceInfoSubTable);
    }
}

