[Form]
public final class VendInvoice4PaymJournal_RU extends FormRun
{
    InventDimCtrl_Frm_Mov           inventDimFormSetup;
}

    public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = invoice4PaymTrans;
    }

    return docCursor;
}

    void updateDesign(InventDimFormDesignUpdate mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
           inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
           inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
           inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
           inventDimFormSetup.formSetControls(false);
           break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

        public void init()
{
    QueryBuildDataSource  queryDataSourceLink;

    super();

    if (element.args().dataset() == tableNum(PurchTable))
    {
        this.query().dataSourceTable(tableNum(VendInvoice4PaymJour_RU)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tableNum(VendInvoice4PaymJour_RU)).addDataSource(tableNum(VendInvoice4PaymPurchLink_RU));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(VendInvoice4PaymPurchLink_RU, OrigPurchId),
                                        element.args().record(),
                                        fieldNum(PurchTable, PurchId));
    }

    if (element.args().dataset() == tableNum(VendInvoiceJour))
    {
        queryDataSourceLink = this.query().dataSourceTable(tableNum(VendInvoice4PaymJour_RU));
        queryDataSourceLink.clearDynalinks();
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, RecId));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, PurchId));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, Invoice4PaymId));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, Invoice4PaymDate));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, CurrencyCode));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, Invoice4PaymAmount));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, LanguageId));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, NumberSequenceGroupId));
        queryDataSourceLink.addGroupByField(fieldNum(VendInvoice4PaymJour_RU, InternalInvoice4PaymId));

        queryDataSourceLink = queryDataSourceLink.addDataSource(tableNum(CostAllocationHistory_RU));
        queryDataSourceLink.addLink(fieldNum(VendInvoice4PaymJour_RU, RecId), fieldNum(CostAllocationHistory_RU, CustVendInvoice4PaymJourRecId));

        queryDataSourceLink = this.query().dataSourceTable(tableNum(CostAllocationHistory_RU)).addDataSource(tableNum(VendTrans));
        queryDataSourceLink.addLink(fieldNum(CostAllocationHistory_RU, IndebtednessTransRecId), fieldNum(VendTrans, RecId));

        queryDataSourceLink = this.query().dataSourceTable(tableNum(VendTrans)).addDataSource(tableNum(VendInvoiceJour));
        queryDataSourceLink.addLink(fieldNum(VendTrans, Voucher), fieldNum(VendInvoiceJour, LedgerVoucher));
        queryDataSourceLink.addLink(fieldNum(VendTrans, AccountNum), fieldNum(VendInvoiceJour, InvoiceAccount));
        queryDataSourceLink.addLink(fieldNum(VendTrans, TransDate), fieldNum(VendInvoiceJour, InvoiceDate));

        queryDataSourceLink.addRange(fieldNum(VendInvoiceJour, RecId)).value(SysQuery::value(element.args().record().RecId));
    }
