    // Tax invoice date
    vendInvoiceJour_DocumentDate.label("@SYS4082542");
    // Date of tax invoice.
    vendInvoiceJour_DocumentDate.helpText("@SYS4082543");
    // Invoice number
    vendInvoiceJour_InvoiceNum.label("@SYS4726");
}

private void updateWorkflowIconForHeader()
{
    VendInvoiceInfoTable localvendInvoiceInfoTable = VendInvoiceInfoTable::findSourceDocumentHeader(vendInvoiceJour.SourceDocumentHeader);

    //The workflow icon on header should be visible only if there is a
    //workflow history available this invoice
    if (Workflow::findTrackingStatusForDocument(localvendInvoiceInfoTable))
    {
        VendorInvoiceWorkflowDropDialog.visible(true);
    }
    else
    {
        VendorInvoiceWorkflowDropDialog.visible(false);
    }
}

