public void clicked()
{
    VendTrans vendTrans;
    LedgerJournalVoucherChanged ledgerJournalVoucherChanged;            

    select firstonly vendTrans
        exists join ledgerJournalVoucherChanged
        where vendTrans.Voucher == ledgerJournalVoucherChanged.FromVoucher
            && vendTrans.TransDate == ledgerJournalVoucherChanged.FromDate
            && LedgerJournalVoucherChanged.ToVoucher == vendInvoiceJour.LedgerVoucher
            && LedgerJournalVoucherChanged.ToDate == vendInvoiceJour.InvoiceDate;

    if (vendTrans)
    {
        Args args = new Args();
        args.record(vendTrans);
        new MenuFunction(menuitemDisplayStr(VendTrans), MenuItemType::Display).run(args);
    }
    else
    {
         super();
    }
}

