[Hookable(false)]
public void clicked()
{
    // VendInvoiceInfoTable is a transient table from which invoices may be deleted. Above checks will ensure that this
    // menu item is not visible if no invoice is found for the selected journal, so it is safe to assume in this clicked
    // event that an invoice exists for the selected journal and that invoice was in automation.
    vendInvoiceInfoTable.data(VendInvoiceInfoTable::findSourceDocumentHeader(vendInvoiceJour.SourceDocumentHeader));
    super();
}

