/// <summary>
/// Determines whether the current <c>VendInvoiceInfoLine</c> table record exists.
/// </summary>
/// <returns>
/// true if the record exists; otherwise, false.
/// </returns>
public boolean checkIfLineExists()
{
    VendInvoiceInfoLine activeInvoiceLine;

    // Verify the invoice line still exists (could have been deleted on posting invoice form)
    select firstonly RecId from activeInvoiceLine where activeInvoiceLine.RecId == vendInvoiceInfoLine.RecId;
    if (activeInvoiceLine.RecId)
    {
        return true;
    }
    else
    {
        // Update the totals only if the invoice still exists (otherwise is cleared in the method call)
        if (this.checkIfTableExists())
        {
            element.updateInvoiceTotals();
        }

        return false;
    }
}

/// <summary>
/// Checks the invoice matching status of the document and then refreshes the datasource.
/// </summary>
public void checkIfMatchedAndRefresh()
{
    if (!VendParameters::find().editAutomaticHeaderMatching())
    {
        vendInvoiceInfoTable.checkIfMatched();
        vendInvoiceInfoTable_ds.reread();
        vendInvoiceInfoTable_ds.refresh();
    }
}

/// <summary>
/// Determines whether the current <c>VendInvoiceInfoTable</c> table record exists.
/// </summary>
/// <returns>
/// true if the record exists; otherwise, false.
/// </returns>
public boolean checkIfTableExists()
{
    VendInvoiceInfoTable activeInvoiceHeader;

    // Only allow field to be modified if the invoice still exists (could have been deleted on posting invoice form)
    select firstonly RecId from activeInvoiceHeader where activeInvoiceHeader.RecId == vendInvoiceInfoTable.RecId;
    if (activeInvoiceHeader.RecId)
    {
        return true;
    }
    else
    {
        // Disable and clear the approval checkbox, disable the other fields (will clear when the table is cleared below)
        approveGroup.enabled(false);
        invoiceMatchingApproval.allowEdit(false);
        purchParmTable_VarianceApproved.value(NoYes::No);

        // Disable the total packing slips and misc charges button and clear the totals
        buttonParmLinePackingSlips.enabled(false);
        miscChargesBtn.enabled(false);
        invoiceTotals_Balance.realValue(0);
        invoiceTotals_TotalDiscount.realValue(0);
        invoiceTotals_MiscCharges.realValue(0);
        invoiceTotals_SalesTax.realValue(0);
        invoiceTotals_RoundOff.realValue(0);
        invoiceTotals_InvoiceAmount.realValue(0);
        invoiceTotals_ComparedMiscCharges.realValue(0);

        vendInvoiceInfoTable.clear();
        vendInvoiceInfoTable_ds.refresh();

        return false;
    }
}

/// <summary>
/// Clears all cached table display or edit methods and refreshes this form's UI elements including matching statuses and totals.
/// </summary>
public void clearCachesAndUpdate()
{
    // Force a write to the line so that the PurchTotals class has up-to-date values to calculate totals from
    vendInvoiceInfoLine_ds.write();

    // Clear all the caches
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, invoiceNetUnitPrice));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, linePriceVarianceAmount));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, priceVarianceStatus));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, priceVariancePercentDisplay));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, purchQtyMatched));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, pdsCWQtyMatched));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, checkIfQuantityMatch));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, expectedPOAmount));

    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, extendedPriceVarianceAmount));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, extendedPriceVariancePercent));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, balanceToInvoice));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, extendedPriceVarianceDisplay));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, lineMatchingPolicy));

    extPriceVarianceAmountMSTCache = new Map(Types::Int64, Types::Real);
    localMatchVarianceStatusCache = new Map(Types::Int64, Types::Enum);

    element.updateInvoiceTotals();
    element.updatePriceAndQtyWarnings();
}

/// <summary>
/// Triggers a refresh of the calling parent form due to potentially changed data on this form.
/// </summary>
public void close()
{
    VendParameters vendParameters = VendParameters::find();

    if (vendParameters.editUseInvoiceMatching() && vendInvoiceInfoTable.RecId)
    {
        if (!vendParameters.editAutomaticHeaderMatching() && vendInvoiceInfoTable.LastMatchVariance == LastMatchVarianceOptions::Unknown)
        {
            vendInvoiceInfoTable.checkIfMatched(true, true);
        }

        SysPolicySourceDocumentRuleViolation localSysPolicySourceDocumentRuleViolation;
        VendInvoiceInfoLine localVendInvoiceInfoLine;

        // Remove the violation records
        delete_from localSysPolicySourceDocumentRuleViolation
            exists join localVendInvoiceInfoLine
                where localVendInvoiceInfoLine.SourceDocumentLine == localSysPolicySourceDocumentRuleViolation.SourceDocumentLine
                && localVendInvoiceInfoLine.TableRefId == vendInvoiceInfoTable.TableRefId
                && localVendInvoiceInfoLine.ParmId == vendInvoiceInfoTable.ParmId;

        delete_from localSysPolicySourceDocumentRuleViolation
            where localSysPolicySourceDocumentRuleViolation.SourceDocumentHeader == vendInvoiceInfoTable.SourceDocumentHeader;
    }

    // Remove the inquiry records or save the pending records
    if (activeInvoiceCreated)
    {
        VendInvoiceInfoTable::moveFromActiveToSaved(vendInvoiceInfoTable);

        // delete created records
        if (purchParmUpdate)
        {
            purchParmUpdate.delete();
        }
    }

    if (!readOnlyField && SysFormRun::hasMethod(element.args().caller(), identifierStr(refresh)))
    {
        element.args().caller().refresh();
    }

    super();
}

