/// <summary>
/// Determines whether the policy violation exist at line or header level.
/// </summary>
/// <returns>
/// true if the record exists; otherwise, false.
/// </returns>
public boolean hasPolicyViolation()
{
    if (element.args().record().TableId == tableNum(VendInvoiceInfoTable))
    {
        policyViolationMessage = VendInvoicePolicyValidation::policyViolationMessage(vendInvoiceInfoLine.RecId, element.args().record().RecId);
    }
    else
    {
        policyViolationMessage = VendInvoicePolicyValidation::policyViolationMessage(vendInvoiceInfoLine.RecId, vendInvoiceInfoTable.RecId);
    }

    if (policyViolationMessage == '')
    {
        return false;
    }
    else
    {
        return true;
    }
}

