/// <summary>
/// Initializes the form.
/// </summary>
/// <exception cref="Exception::Error">
/// The window cannot be opened directly. It must be opened from a parent form that provides callbacks to retrieve a PurchFormLetter and has a Refresh method.
/// The caller must also supply a positioned VendInvoiceInfoTable, VendInvoiceInfoLine, VendTmpInvoiceInfoTable or VendInvoiceInfoTable table.
/// </exception>
/// <remarks>
/// The calling form must pass the current record off of the supplied tables.
/// The calling form must provide a purchFormLetter callback method in order to pass along to the Match packing slips window.
/// The calling form should provide a refresh callback method in order to have changes made on this form reflected on the caller once this form closes.
/// </remarks>
public void init()
{
    VendParameters vendParameters;
    VendInvoiceInfoTable localVendInvoiceInfoTable;
    VendTmpInvoiceInfoTable vendTmpInvoiceInfoTable;
    VendInvoiceInfoLine localVendInvoiceInfoLine;
    ParmId parmId;
    VendInvoicePolicyValidation policyValidation;

    super();

    if (!element.args().caller() || !element.args().record())
    {
        throw error("@SYS22539");
    }

    readOnlyField = element.args().parmEnum() == NoYes::Yes;

    boolean isWorkflowInReview;

    if (element.args().record().TableId == tableNum(VendInvoiceInfoTable))
    {
        VendInvoiceInfoTable vendInvoiceInfoTableInReview = element.args().record();

        isWorkflowInReview = vendInvoiceInfoTableInReview.isDocumentBeingProcessedInWorklow();
    }

    if (readOnlyField || isWorkflowInReview)
    {
        vendInvoiceInfoTable_ds.allowEdit(false);
        vendInvoiceInfoLine_ds.allowEdit(false);
        inventDim_ds.allowEdit(false);
        purchTable_ds.allowEdit(false);
        vendInvoiceMatchingLine_ds.allowEdit(false);
        invoiceMatchingApproval.allowEdit(false);
        buttonParmLinePackingSlips.enabled(false);
    }

    if (isWorkflowInReview)
    {
        invoiceMatchingApproval.allowEdit(true);
    }

    miscChargesBtn.enabled(hasMenuItemAccess(menuitemDisplayStr(MarkupTransInquiry), MenuItemType::Display));

    policyValidation = new VendInvoicePolicyValidation();

    switch (element.args().record().TableId)
    {
        case tableNum(VendInvoiceInfoLine):
            localVendInvoiceInfoLine = element.args().record();
            policyValidation.parmVendInvoiceInfoTable(localVendInvoiceInfoLine.vendInvoiceInfoTable());

            if (element.args().parmObject())
            {
                // Trigger to apply the user's line query filter from the parent form to the lines on this form
                applyLineFilters = true;
            }
            else
            {
                applyLineFilters = false;
            }

            activeInvoiceCreated = false;

            break;

        case tableNum(VendTmpInvoiceInfoTable):
            Debug::assert(readOnlyField);

            vendTmpInvoiceInfoTable = element.args().record();

            localVendInvoiceInfoTable = VendInvoiceInfoTable::findTableRefId(vendTmpInvoiceInfoTable.TableRefId);
            vendInvoiceInfoTable.data(localVendInvoiceInfoTable);
            vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, ParmId)).value(queryValue(localVendInvoiceInfoTable.ParmId));
            vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, TableRefId)).value(queryValue(localVendInvoiceInfoTable.TableRefId));
            activeInvoiceCreated = false;

            break;

        case tableNum(VendInvoiceInfoTable):
            applyLineFilters = false;
            localVendInvoiceInfoTable = element.args().record();
            policyValidation.parmVendInvoiceInfoTable(localVendInvoiceInfoTable);

            if (localVendInvoiceInfoTable.ParmId == '')
            {
                if (localVendInvoiceInfoTable.PoolRecId)
                {
                    purchFormLetter = PurchFormLetter_ApproveJournal::newApprovalFromSavedInvoice(localVendInvoiceInfoTable);
                }
                else
                {
                    purchFormLetter = PurchFormLetter_Invoice::newFromSavedInvoice(localVendInvoiceInfoTable);
                }
                purchFormLetter.initNewPurchParmUpdate();
                purchParmUpdate = purchFormLetter.purchParmUpdate();
                purchFormLetter.chooseLines(true, vendInvoiceInfoTable);
                parmId = purchFormLetter.parmId();

                PurchFormLetter_Invoice purchformletter_invoice = purchFormLetter as PurchFormLetter_Invoice;
                if (purchformletter_invoice.isSetActiveInvoiceFlag(VendActiveInvoiceFlags::InvoiceInUse))
                {
                    checkFailed("@AccountsPayable:VendInvoiceInUseByAnotherUser");
                    activeInvoiceCreated = false;
                }
                else
                {
                    activeInvoiceCreated = true;
                }

                // Update the query on this form to show the new VendInvoiceInfoTable record created
                vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, ParmId)).value(queryValue(parmId));
                vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).clearDynalinks();
            }
            else
            {
                activeInvoiceCreated = false;
            }
            break;

        default:
            throw error("@SYS22539");
    }

    // Only show the Approval fields if setup requires approval
    vendParameters = VendParameters::find();
    invoiceMatchingApproval.visible(vendParameters.PostInvoiceMatchDiscrepancies == InvoiceMatchPostingOption::RequireApproval);
    warnInvoiceTotals.visible(vendParameters.UseTotalPriceMatching == NoYes::Yes);
    boolean isFeatureEnabled = FeatureStateProvider::isFeatureEnabled(VendInvoiceTotalsMatchingDetailFeature::instance());
    InvoiceMatchingStatusColumnLabel.visible(isFeatureEnabled);
    Warnbalances.visible(isFeatureEnabled && vendParameters.UseTotalPriceMatching == NoYes::Yes);
    WarnTotalDiscount.visible(isFeatureEnabled && vendParameters.UseTotalPriceMatching == NoYes::Yes);
    WarnCharges.visible(isFeatureEnabled && vendParameters.UseTotalPriceMatching == NoYes::Yes);
    WarnSalesTax.visible(isFeatureEnabled && vendParameters.UseTotalPriceMatching == NoYes::Yes);
    WarnInvoiceAmount.visible(isFeatureEnabled && vendParameters.UseTotalPriceMatching == NoYes::Yes);
    buttonPurchInvoiceTotalsMatchingDetails.visible(vendParameters.UseTotalPriceMatching == NoYes::Yes);

    //Enable policy violation if use invoice matcing is turned on
    if (vendParameters.editUseInvoiceMatching())
    {
        policyViolation.enabled(true);

        // invoice policy validations
        policyValidation.checkVendorInvoicePolicyValidation();
    }
    else
    {
        policyViolation.enabled(false);
    }

    switch (vendParameters.useExtendedPriceMatching())
    {
        case ExtendedPriceMatching::None:

            purchParmLine_ExtendedPriceVarianceAmount.visible(false);
            purchParmLine_ExtendedPriceVariancePercent.visible(false);
            vendInvoiceMatchingLine_ExtendedPriceTolerancePercentage.visible(false);
            purchParmLine_ExtendedPriceVarianceAmountMST.visible(false);
            vendInvoiceMatchingLine_ExtendedPriceToleranceAmount.visible(false);
            poBalanceToInvoice.visible(false);

            break;

        case ExtendedPriceMatching::Amount:

            purchParmLine_ExtendedPriceVariancePercent.visible(false);
            vendInvoiceMatchingLine_ExtendedPriceTolerancePercentage.visible(false);

            break;

        case ExtendedPriceMatching::Percentage:

            purchParmLine_ExtendedPriceVarianceAmountMST.visible(false);
            vendInvoiceMatchingLine_ExtendedPriceToleranceAmount.visible(false);

            break;
    }

    if (vendParameters.UseMiscChargeMatching == NoYes::No)
    {
        totalsComparedMiscChargesRowLabel.visible(false);
        invoiceTotals_ComparedMiscCharges.visible(false);
        warnMiscCharge.visible(false);
        miscChargesBtn.visible(false);
    }

    // Reformat the grid based upon the current inventory dimensions fields selected to be displayed
    element.updateDesign(InventDimFormDesignUpdate::Init);

    showAllLines = true;
}

