/// <summary>
/// Gets or sets the <c>LastMatchVarianceOptions</c> for Discount
/// </summary>
/// <param name = "_unitPriceMatchingStatus">The <c>LastMatchVarianceOptions</c> value to be used to determine the proper status</param>
/// <returns>The <c>LastMatchVarianceOptions</c> value</returns>
public display LastMatchVarianceOptions matchingVarianceDiscount(LastMatchVarianceOptions _discountMatchingStatus = LastMatchVarianceOptions::Unknown)
{
    if (!prmisdefault(_discountMatchingStatus))
    {
        discountMatchingStatus = _discountMatchingStatus;
    }
    return discountMatchingStatus != LastMatchVarianceOptions::Warning ? unitPriceMatchingStatus : vendInvoiceInfoLine.LineDisc == vendInvoiceMatchingLine.ExpectedLineDisc ? LastMatchVarianceOptions::OK : LastMatchVarianceOptions::Warning;
}

