/// <summary>
/// Gets or sets the <c>LastMatchVarianceOptions</c> for Net Unit Price
/// </summary>
/// <param name = "_unitPriceMatchingStatus">The <c>LastMatchVarianceOptions</c> value to be used to determine the proper status</param>
/// <returns>The <c>LastMatchVarianceOptions</c> value</returns>
public display LastMatchVarianceOptions matchingVarianceNetUnitPrice(LastMatchVarianceOptions _netUnitPriceMatchingStatus = LastMatchVarianceOptions::Unknown)
{
    if (!prmisdefault(_netUnitPriceMatchingStatus))
    {
        netUnitPriceMatchingStatus = _netUnitPriceMatchingStatus;
    }
    return netUnitPriceMatchingStatus != LastMatchVarianceOptions::Warning ? unitPriceMatchingStatus : vendInvoiceInfoLine.invoiceNetUnitPrice() == vendInvoiceMatchingLine.expectedNetUnitPrice() ? LastMatchVarianceOptions::OK : LastMatchVarianceOptions::Warning;
}

