/// <summary>
/// Returns the current PurchFormLetter class.
/// </summary>
/// <returns>
/// The PurchFormLetter class held by the parent of this form.
/// </returns>
/// <remarks>
/// Required when opening the "Match packing slips to invoice" form.
/// </remarks>
private PurchFormLetter purchFormLetter()
{
    if (purchFormLetter)
    {
        return purchFormLetter;
    }
    else if (SysFormRun::hasMethod(element.args().caller(), identifierStr(purchFormLetter)))
    {
        return element.args().caller().purchFormLetter();
    }
    else
    {
        return null;
    }
}

/// <summary>
/// Prevents removal of the user's line query filter being applied from the parent form to the lines on this form due to multiple data source executeQuery()'s.
/// </summary>
public void run()
{
    boolean prevValue = applyLineFilters;

    // Prevent the line filters from being replaced in the PurchParmLine_ds.active() method
    applyLineFilters = false;
    super();
    applyLineFilters = prevValue;

    // Update the # of decimals on the Invoice and PO net unit price fields
    purchParmLine_InvoiceNetUnitPrice.noOfDecimalsValue(purchParmLine_PurchPrice.noOfDecimals() + 4);
    vendInvoiceMatchingLine_ExpectedNetUnitPrice.noOfDecimalsValue(purchParmLine_InvoiceNetUnitPrice.noOfDecimals());
}

public void setNonPOFields()
{
    if (vendInvoiceInfoLine.isNonPO())
    {
        // Set the expected values so that they are blank and not 0.00
        vendInvoiceMatchingLine_ExpectedPrice.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedPriceUnit.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedMarkup.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedLineDisc.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedLinePercent.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedMultLnDisc.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedMultLnPercent.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedLineAmount.showZero(RepShowZero::No);
        vendInvoiceMatchingLine_ExpectedNetUnitPrice.showZero(RepShowZero::No);
        purchParmLine_BalanceToInvoice.showZero(RepShowZero::No);

        buttonParmLinePackingSlips.enabled(false);
    }
    else
    {
        // Set the expected values so that they show 0.00
        vendInvoiceMatchingLine_ExpectedPrice.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedPriceUnit.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedMarkup.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedLineDisc.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedLinePercent.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedMultLnDisc.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedMultLnPercent.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedLineAmount.showZero(RepShowZero::Yes);
        vendInvoiceMatchingLine_ExpectedNetUnitPrice.showZero(RepShowZero::Yes);
        purchParmLine_BalanceToInvoice.showZero(RepShowZero::Yes);

        buttonParmLinePackingSlips.enabled(!readOnlyField);
    }
}

/// <summary>
/// Unapproves the invoice and refreshes the PO line values.
/// </summary>
/// <param name="_isQtyUpdated">
/// A Boolean value that specifies whether the quantity is updated; optional.
/// </param>
public void unapprove(boolean _isQtyUpdated = true)
{
    int pos;

    if (vendInvoiceInfoTable.VarianceApproved == NoYes::Yes)
    {
        // Must wrap with a transaction, required when editing from VendInvoiceInfo list page
        ttsbegin;
        vendInvoiceInfoTable.approveVariance(false, true, _isQtyUpdated);
        ttscommit;

        vendInvoiceInfoTable_ds.refresh();
        purchParmTable_VarianceApproved.value(NoYes::No);
        approveGroup.enabled(false);

        // An unapprove could have caused PO Line values to change on any line. Retain the current selected row for the user.
        pos = vendInvoiceInfoLine_ds.getPosition();
        vendInvoiceInfoLine_ds.research();
        vendInvoiceInfoLine_ds.setPosition(pos);

        // Caller is responsible to recalculate warnings.
    }
}

/// <summary>
/// Reformats the grid based upon the current inventory dimensions fields selected to be displayed.
/// </summary>
/// <param name="_mode">
/// The update mode requested by the caller.
/// </param>
/// <exception cref="Exception::Error">
/// The update mode requested is not supported by this form.
/// </exception>
void updateDesign(InventDimFormDesignUpdate _mode)
{
    Set validInventDimDSset;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :

            if (!inventDimFormSetup)
            {
                validInventDimDSset = new Set(Types::String);
                validInventDimDSset.add(inventDim_ds.name());
                inventDimFormSetup = InventDimCtrl_Frm_Mov_VendInvoice::newFromFormInvoice(element, vendInvoiceInfoLine);
            }

        case InventDimFormDesignUpdate::Active :
        case InventDimFormDesignUpdate::FieldChange :

            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(vendInvoiceInfoLine.ItemId));
            inventDimFormSetup.formSetControls(_mode == InventDimFormDesignUpdate::Active);
            break;

        default : throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
/// Updates the invoice totals group after a new line is selected or a change is made to any price within a line.
/// </summary>
/// <param name="_clear">
/// Indicates whether the statuses should be cleared instead of set to passed or failed.
/// </param>
public void updateInvoiceTotals(boolean _clear = false)
{
    boolean isMatched;
    LastMatchVarianceOptions matchStatus;
    AmountCur totalActiveInvoiceCalculatedAmount = 0;

    // Only update if the invoice has not been deleted
    if (_clear || vendInvoiceInfoTable.RecId == 0)
    {
        this.matchingVarianceMiscCharge(LastMatchVarianceOptions::Unknown);
    }
    else
    {
        purchTotals = PurchTotals::newParmTable(vendInvoiceInfoTable, PurchUpdate::All, vendInvoiceInfoTable.ParmId, '', vendInvoiceInfoTable.Ordering);
        purchTotals.calc();

        invoiceTotals_Balance.realValue(purchTotals.purchBalance());
        invoiceTotals_TotalDiscount.realValue(purchTotals.purchEndDisc());
        invoiceTotals_MiscCharges.realValue(purchTotals.purchMarkup());
        invoiceTotals_SalesTax.realValue(purchTotals.purchTaxTotal());
        invoiceTotals_RoundOff.realValue(purchTotals.purchRoundOff());
        invoiceTotals_InvoiceAmount.realValue(purchTotals.purchTotalAmount());

        if (purchTotals.recordListPurchLine().len() == 0)
        {
            this.matchingVarianceMiscCharge(LastMatchVarianceOptions::Unknown);
        }
        else
        {
            [isMatched, matchStatus, totalActiveInvoiceCalculatedAmount] = MarkupTmpTotals::totalInvoiceMatchingInfo(vendInvoiceInfoTable, NoYes::Yes);
            this.matchingVarianceMiscCharge(matchStatus);
        }

        // TotalPriceVarianceAmount is auto-linked to the VendInvoiceInfoTable and does not need to be manually updated here. (PurchTotals is unaware of the field as well)
    }

    invoiceTotals_ComparedMiscCharges.realValue(totalActiveInvoiceCalculatedAmount);
}

/// <summary>
/// Updates the matching statuses in the Price and Quantity groups after a new line is selected or a change is made to any price within a line.
/// </summary>
/// <param name="_clear">
/// Indicates whether the statuses should be cleared instead of set to checked or warning.
/// </param>
/// <param name="_qtyAndInfobarOnly">
/// Indicates whether just the quantity/infobar should be updated.
/// </param>
public void updatePriceAndQtyWarnings(boolean _clear = false, boolean _qtyAndInfobarOnly = false)
{
    boolean onlyNonPOLines;

    LastMatchVarianceOptions quantityMatch;
    LastMatchVarianceOptions priceMatch;
    LastMatchVarianceOptions extPriceMatch;

    // Determine if anyting other than an empty status should be shown
    if (_clear || vendInvoiceInfoLine.RecId == 0)
    {
        // show empty status
        quantityMatch = LastMatchVarianceOptions::Unknown;
        priceMatch = LastMatchVarianceOptions::Unknown;
        extPriceMatch = LastMatchVarianceOptions::Unknown;
    }
    else
    {
        // Status shown should be Passed when matching status at line is ok
        // Status shown should be empty when matching status at line is not applicable
        // Status shown could be Failed or Passed when status at line is failed
        quantityMatch = vendInvoiceInfoLine.checkIfQuantityMatch();
        priceMatch = vendInvoiceInfoLine.priceVarianceStatus();
        extPriceMatch = vendInvoiceInfoLine.extendedPriceVarianceStatus(vendInvoiceInfoTable);
    }

    if (!_qtyAndInfobarOnly)
    {
        // Price group
        this.matchingVarianceUnitPrice(priceMatch);
        this.matchingVariancePriceUnit(priceMatch);
        this.matchingVariancePurchaseMiscCharges(priceMatch);
        this.matchingVarianceDiscount(priceMatch);
        this.matchingVarianceDiscountPercent(priceMatch);
        this.matchingVarianceMultilineDiscount(priceMatch);
        this.matchingVarianceMultilineDiscountPercent(priceMatch);
        this.matchingVarianceNetAmount(priceMatch);
        this.matchingVarianceNetUnitPrice(priceMatch);

        // Extended price group
        this.matchingVarianceExtendedPrice(extPriceMatch);

        onlyNonPOLines = (purchTotals.recordListPurchLine().len() == 0);

        this.matchingVarianceInvoiceTotals(_clear || onlyNonPOLines ? LastMatchVarianceOptions::Unknown : VendInvoiceInfoTable.checkIfMatchedTotals(purchTotals) ? LastMatchVarianceOptions::OK : LastMatchVarianceOptions::Warning);
        if (FeatureStateProvider::isFeatureEnabled(VendInvoiceTotalsMatchingDetailFeature::instance()))
        {
            this.updateInvoiceTotalsMatchStatus(onlyNonPOLines);
        }
    }

    // Quantity group
    this.matchingVarianceQuantity(quantityMatch);

    // Status controls in top group
    if (priceMatch == LastMatchVarianceOptions::Unknown)
    {
        UnitPriceMatchErrors.visible(false);
    }
    else
    {
        UnitPriceMatchErrors.visible(true);
        this.hasUnitPriceMatchErrors(priceMatch == LastMatchVarianceOptions::Warning);
    }

    if (quantityMatch == LastMatchVarianceOptions::Unknown)
    {
        QuantityMatchErrors.visible(false);
    }
    else
    {
        QuantityMatchErrors.visible(true);
        this.hasQuantityMatchErrors(quantityMatch == LastMatchVarianceOptions::Warning);
    }

    if (extPriceMatch == LastMatchVarianceOptions::Unknown)
    {
        ExtendedPriceMatchErrors.visible(false);
    }
    else
    {
        ExtendedPriceMatchErrors.visible(true);
        this.hasExtPriceMatchErrors(extPriceMatch == LastMatchVarianceOptions::Warning);
    }

    if (!this.hasPolicyViolation())
    {
        PolicyViolationErrors.visible(false);
    }
    else
    {
        PolicyViolationErrors.visible(true);
        this.hasPolicyViolationErrors(true);
    }
}

private void updateInvoiceTotalsMatchStatus(boolean _onlyNonPOLines)
{
    boolean isVariance;
    vendInvoiceMatching vendinvoiceMatching = vendInvoiceMatching::find(VendInvoiceInfoTable.ParmId, VendInvoiceInfoTable.TableRefId);
    VendParameters vendParameters = VendParameters::find();

    // Balance
    if (!_onlyNonPOLines && vendParameters.UsePriceMatchingForSubTotalAmount)
    {
        isVariance = VendInvoiceMatching::isTotalPriceVarianceAboveToleranceForSubTotalAmount(purchTotals.purchBalance(),
                                                vendInvoiceMatching.ExpectedBalance,
                                                vendInvoiceMatching.PriceTolerancePercentageForSubTotalAmount);

        this.matchingVarianceBalance(isVariance ? LastMatchVarianceOptions::Warning : LastMatchVarianceOptions::OK);
    }
    else
    {
        this.matchingVarianceBalance();
    }

    // Total discount
    if (!_onlyNonPOLines && vendParameters.UsePriceMatchingForTotalDiscount)
    {
        isVariance = VendInvoiceMatching::isTotalPriceVarianceAboveToleranceForTotalDiscount(
                                                purchTotals.purchEndDisc(),
                                                vendInvoiceMatching.ExpectedTotalDiscount,
                                                vendInvoiceMatching.PriceTolerancePercentageForTotalDiscount,
                                                true); // Negative balance

        this.matchingVarianceTotalDiscount(isVariance ? LastMatchVarianceOptions::Warning : LastMatchVarianceOptions::OK);
    }
    else
    {
        this.matchingVarianceTotalDiscount();
    }

    // Misc charges
    if (!_onlyNonPOLines && vendParameters.UsePriceMatchingForCharges)
    {
        isVariance = VendInvoiceMatching::isTotalPriceVarianceAboveToleranceForCharges(
                                                purchTotals.purchMarkup(),
                                                vendInvoiceMatching.ExpectedMarkup,
                                                vendInvoiceMatching.PriceTolerancePercentageForCharges);

        this.matchingVarianceCharges(isVariance ? LastMatchVarianceOptions::Warning : LastMatchVarianceOptions::OK);
    }
    else
    {
        this.matchingVarianceCharges();
    }

    // Sales tax
    if (!_onlyNonPOLines && vendParameters.UsePriceMatchingForSalesTax)
    {
        isVariance = VendInvoiceMatching::isTotalPriceVarianceAboveToleranceForSalesTax(
                                                purchTotals.purchTaxTotal(),
                                                vendInvoiceMatching.ExpectedSalesTax,
                                                vendInvoiceMatching.PriceTolerancePercentageForSalesTax);

        this.matchingVarianceSalesTax(isVariance ? LastMatchVarianceOptions::Warning : LastMatchVarianceOptions::OK);
    }
    else
    {
        this.matchingVarianceSalesTax();
    }

    // Invoice amount
    if (!_onlyNonPOLines && vendParameters.UsePriceMatchingForInvoiceAmount)
    {
        isVariance = VendInvoiceMatching::isTotalPriceVarianceAboveToleranceForInvoiceAmount(
                                                purchTotals.purchTotalAmount(),
                                                vendInvoiceMatching.ExpectedDocumentAmount,
                                                vendInvoiceMatching.PriceTolerancePercentageForInvoiceAmount);

        this.matchingVarianceInvoiceAmount(isVariance ? LastMatchVarianceOptions::Warning : LastMatchVarianceOptions::OK);
    }
    else
    {
        this.matchingVarianceInvoiceAmount();
    }

    // Round-Off
    if (!_onlyNonPOLines && vendParameters.UsePriceMatchingForRoundOff)
    {
        isVariance = VendInvoiceMatching::isTotalPriceVarianceAboveToleranceForRoundOff(
                                                purchTotals.purchRoundOff(),
                                                vendInvoiceMatching.PriceToleranceAmountForInvoiceRoundOff);

        this.matchingVarianceRoundOff(isVariance ? LastMatchVarianceOptions::Warning : LastMatchVarianceOptions::OK);
    }
    else
    {
        this.matchingVarianceRoundOff();
    }
}

