void markupTransMatchingChanged()
{
    Object dataSource;

    if (FormDataUtil::isFormDataSource(element.args().record()))
    {
        dataSource = FormDataUtil::getFormDataSource(element.args().record());
        if (dataSource)
        {
            if (formDataSourceHasMethod(dataSource, identifierStr(markupTransMatchingChanged)))
            {
                dataSource.markupTransMatchingChanged();
            }
        }
    }
}

/// <summary>
/// Responds to a write request on the VendInvoiceInfoTable table by ensuring the record has not been deleted by another process.
/// </summary>
public void write()
{
    if (!inVendInvoiceInfoTableWrite)
    {
        if (element.checkIfTableExists())
        {
            super();
        }
        else
        {
            vendInvoiceInfoTable.clear();

            // Prevent recursion due to .research() forcing a .leaveRecord() which calls back into .write()
            inVendInvoiceInfoTableWrite = true;
            vendInvoiceInfoTable_ds.research();
            inVendInvoiceInfoTableWrite = false;
        }
    }
}

/// <summary>
/// Updates all matching statuses, totals, approval checkbox, approval comment.
/// </summary>
/// <returns>
/// 1 if successful; otherwise, 0.
/// </returns>
public int active()
{
    int ret = super();

    purchParmTable_VarianceApproved.value(vendInvoiceInfoTable.VarianceApproved);
    comment.text(vendInvoiceInfoTable.VarianceComment);
    approveGroup.enabled(vendInvoiceInfoTable.VarianceApproved);
    element.updateInvoiceTotals();

    return ret;
}

/// <summary>
/// Responds to a field change on the VendInvoiceInfoTable table by unapproving the invoice and refreshing the form.
/// </summary>
/// <param name="_fieldId">
/// The ID of the field that changed.
/// </param>
/// <param name="_unapprove">
/// true if the change should unapprove the invoice; otherwise, false.
/// </param>
public void modifiedField(FieldId _fieldId, boolean _unapprove)
{
    if (_unapprove)
    {
        element.unapprove();
    }

    element.clearCachesAndUpdate();
}

public display LastMatchVarianceOptions localMatchVarianceStatus(VendInvoiceInfoLine _vendInvoiceInfoLine)
{
    LastMatchVarianceOptions matchingVarianceStatus;

    if (!localMatchVarianceStatusCache)
    {
        localMatchVarianceStatusCache = new Map(Types::Int64, Types::Enum);
    }

    if (!localMatchVarianceStatusCache.exists(_vendInvoiceInfoLine.RecId))
    {
        matchingVarianceStatus = _vendInvoiceInfoLine.matchingVarianceStatus(vendInvoiceInfoTable);

        localMatchVarianceStatusCache.insert(_vendInvoiceInfoLine.RecId, matchingVarianceStatus);
    }
    else
    {
        matchingVarianceStatus = localMatchVarianceStatusCache.lookup(_vendInvoiceInfoLine.RecId);
    }

    return matchingVarianceStatus;
}

public display LastMatchVarianceOptions extPriceVarianceStatus()
{
    return vendInvoiceInfoLine.extendedPriceVarianceStatus(vendInvoiceInfoTable);
}

public display ExtendedPriceVarianceAmountMST extPriceVarianceAmountMST(VendInvoiceInfoLine _vendInvoiceInfoLine)
{
    ExtendedPriceVarianceAmountMST amountMST;

    if (!extPriceVarianceAmountMSTCache)
    {
        extPriceVarianceAmountMSTCache = new Map(Types::Int64, Types::Real);
    }

    if (!extPriceVarianceAmountMSTCache.exists(_vendInvoiceInfoLine.RecId))
    {
        amountMST = _vendInvoiceInfoLine.extendedPriceVarianceAmountMST(vendInvoiceInfoTable);

        extPriceVarianceAmountMSTCache.insert(_vendInvoiceInfoLine.RecId, amountMST);
    }
    else
    {
        amountMST = extPriceVarianceAmountMSTCache.lookup(_vendInvoiceInfoLine.RecId);
    }

    return amountMST;
}

/// <summary>
/// Responds to a write request on the VendInvoiceInfoLine table by ensuring the record has not been deleted by another process.
