{
    if (!inVendInvoiceInfoLineWrite)
    {
        if (element.checkIfLineExists())
        {
            super();
        }
        else
        {
            vendInvoiceInfoLine.clear();

            // Prevent recursion due to .research() forcing a .leaveRecord() which calls back into .write()
            inVendInvoiceInfoLineWrite = true;
            vendInvoiceInfoLine_ds.research();
            inVendInvoiceInfoLineWrite = false;
        }
    }
}

/// <summary>
/// Refresh the joined VendInvoiceMatchingLine table's data source to clear the Purchase order column in case no rows are returned from the query.
/// </summary>
public void executeQuery()
{
    super();

    vendInvoiceMatchingLine_ds.refresh();

    // Clear the matching statuses if no records are displayed
    if (vendInvoiceInfoTable.RecId && !vendInvoiceInfoLine.RecId)
    {
        element.updatePriceAndQtyWarnings(true);
        element.updateInvoiceTotals(true);
    }
}

/// <summary>
/// Responds to a field change on the VendInvoiceInfoLine table by unapproving the invoice and refreshing the form.
