    int ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);
    element.updatePriceAndQtyWarnings();

    element.setNonPOFields();

    return ret;
}

/// <summary>
/// Caches the display or edit methods on the VendInvoiceInfoLine table data source for improved form performance.
/// </summary>
public void init()
{
    Query q;

    super();

    // Apply the user's line query filter from the parent form to the lines on this form
    if (applyLineFilters)
    {
        applyLineFilters = false;
        q = new Query(this.query());
        SysQuery::mergeRanges(q, new Query(element.args().parmObject()));
        vendInvoiceInfoLine_ds.queryRun().query(q);
    }

    // Note: VendInvoiceInfoLine.matchingVarianceStatus cannot be cached via cacheAddMethod as it causes an error on form open
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, invoiceNetUnitPrice));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, linePriceVarianceAmount));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, priceVarianceStatus));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, priceVariancePercentDisplay));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, purchQtyMatched));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, expectedPOAmount));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, extendedPriceVarianceAmount));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, extendedPriceVariancePercent));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, balanceToInvoice));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, extendedPriceVarianceDisplay));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, lineMatchingPolicy));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, pdsCWQtyMatched));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, checkIfQuantityMatch));
}

public boolean validateWrite()
{
    boolean ret;

    // Skip validation for non-PO lines which will not have a VendInvoiceMatchingLine record
    if (vendInvoiceMatchingLine.RecId)
    {
        ret = super();
    }

    return ret;
}

/// <summary>
/// Shows all lines for the current invoice, regardless if they have matching errors.
/// </summary>
public void clicked()
{
    QueryBuildDataSource qbds = vendInvoiceInfoLine_ds.queryRun().query().dataSourceTable(tableNum(VendInvoiceInfoLine));
    QueryBuildRange qbr;

    if (!showAllLines)
    {
        showAllLines = true;

        // Remove the hidden filter that restricted to only include lines that had match errors (using hidden RecId's OR'd into the user's query filter)
        qbr = qbds.findRange(fieldNum(VendInvoiceInfoLine, RecId));
        while (qbr)
        {
            if (qbr.status() == RangeStatus::Hidden)
            {
                qbds.clearRange(fieldNum(VendInvoiceInfoLine, RecId));
                qbr = qbds.findRange(fieldNum(VendInvoiceInfoLine, RecId));
            }
        }

        // Rerun the user filtered query with our previous additions removed
        vendInvoiceInfoLine_ds.research();
    }

    super();
}

/// <summary>
/// Shows only lines with matching errors for the current invoice.
/// </summary>
public void clicked()
{
    QueryBuildDataSource qbds = vendInvoiceInfoLine_ds.queryRun().query().dataSourceTable(tableNum(VendInvoiceInfoLine));
    QueryBuildRange qbr;
    QueryRun qr;
    VendInvoiceInfoLine activeInvoiceLine;
    boolean foundOne = false;

    if (showAllLines)
    {
        showAllLines = false;

        // Add a hidden filter to show only the lines that have match errors (using hidden RecId's OR'd into the user's query filter)
        qr = new QueryRun(vendInvoiceInfoLine_ds.queryRun().query());
        while (qr.next())
        {
            activeInvoiceLine = qr.get(tableNum(VendInvoiceInfoLine));
            if (activeInvoiceLine.matchingVarianceStatus(vendInvoiceInfoTable) == LastMatchVarianceOptions::Warning)
            {
                qbr = qbds.addRange(fieldNum(VendInvoiceInfoLine, RecId));
                qbr.status(RangeStatus::Hidden);
                qbr.value(queryValue(activeInvoiceLine.RecId));
                foundOne = true;
            }
        }

        if (!foundOne)
        {
            // Set up an empty query (restricts to a RecId of 0) if no lines have errors
            qbr = qbds.addRange(fieldNum(VendInvoiceInfoLine, RecId));
            qbr.status(RangeStatus::Hidden);
            qbr.value(queryValue(0));
        }

        // Rerun the user filtered query with our additions
        vendInvoiceInfoLine_ds.research();
    }

    super();
}

void clicked()
{
    Args args;
    Form purchJournalMatch_PackingSlip;
    FormRun formRun;

    super();

    purchJournalMatch_PackingSlip = new Form(formStr(VendJournalMatch_PackingSlip));

    // Set up the args to create and pass to the form
    args = new Args();
    args.caller(element);
    args.record(vendInvoiceInfoLine);
    args.object(purchJournalMatch_PackingSlip);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    // Refresh the PurchQtyMatched field that may have changed from matching/unmatching packing slips
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, purchQtyMatched));
    vendInvoiceInfoLine_ds.reread();
    vendInvoiceInfoLine_ds.refresh();

    // Update the Quantity group and infobar
    element.updatePriceAndQtyWarnings(false, true);
}

