/// <summary>
/// Approves or unapproves the invoice.
/// </summary>
public void clicked()
{
    // Only process if the invoice still exists
    if (element.checkIfTableExists())
    {
        super();

        if (purchParmTable_VarianceApproved.value() == NoYes::Yes)
        {
            // Must wrap with a transaction, required when editing from VendInvoiceInfo list page
            ttsbegin;
            vendInvoiceInfoTable.approveVariance(true, true);
            ttscommit;
        }
        else
        {
            element.unapprove();
            element.clearCachesAndUpdate();
        }

        approveGroup.enabled(vendInvoiceInfoTable.VarianceApproved);

        vendInvoiceInfoTable_ds.rereadReferenceDataSources();
    }
}

/// <summary>
/// Update datasource table once the approval comment is modified when workflow in review.
/// </summary>
/// <returns>
/// true if the contents have been modified; otherwise, false.
/// </returns>
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        VendInvoiceInfoTable localVendInvoiceInfoTable = vendInvoiceInfoTable_ds.cursor();
        localVendInvoiceInfoTable.VarianceComment = this.text();
    }

    return ret;
}
