Method.Source:
[Form]
public class VendInvoicePostingHistory extends FormRun
{
    InventDimCtrl_Frm_Mov inventDimFormSetup;
    boolean useInvoiceMatching;

}

void init()
{
    DocumentStatus documentStatus;
    QueryRun queryInForm;
    QueryFilter queryFilter;
    QueryBuildDataSource vendInvoiceInfoTableDataSource;

    super();

    if (!element.args().parmEnum())
    {
        throw error("@SYS22539");
    }

    documentStatus = element.args().parmEnum();
    queryInForm = new QueryRun(vendInvoiceInfoTable_ds.query());
    vendInvoiceInfoTableDataSource = queryInForm.query().dataSourceTable(tablenum(VendInvoiceInfoTable));

    vendInvoiceInfoTableDataSource.addRange(fieldNum(VendInvoiceInfoTable, Ordering)).value(queryValue(documentStatus));
    vendInvoiceInfoTableDataSource.addRange(fieldNum(VendInvoiceInfoTable, ParmId)).value(queryValue('>0'));
    vendInvoiceInfoTable_ds.query(queryInForm.query());

    // Explicitly add and hide the VendInvoiceInfoTable.ParmId filter in order to keep it from defaulting on the form filter
    queryFilter = vendInvoiceInfoTable_ds.query().addQueryFilter(vendInvoiceInfoTableDataSource, fieldStr(VendInvoiceInfoTable, ParmId));
    queryFilter.status(RangeStatus::Hidden);

    useInvoiceMatching = VendParameters::find(false).editUseInvoiceMatching(false) == NoYes::Yes;
    element.enableMatchingDetailsButton();

    switch (element.args().menuItemName())
    {
        case menuitemDisplayStr(PurchPostingHistoryInvoice):
            element.design().caption("@SYS191320");
            break;

        case menuitemDisplayStr(PurchPostingHistoryInvoiceApproval):
            element.design().caption("@SYS191321");
            break;
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(vendInvoiceInfoLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }
            break;

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

int active()
{
    int ret = super();

    element.enableMatchingDetailsButton(vendInvoiceInfoTable.ParmJobStatus == ParmJobStatus::Executed);
    buttonUpdate.enabled(vendInvoiceInfoTable.ParmJobStatus == ParmJobStatus::Executed);
    buttonErrorLog.enabled(vendInvoiceInfoTable.ParmJobStatus == ParmJobStatus::ContainErrors);

    if (vendInvoiceInfoTable.isDocumentBeingProcessedInWorklow() ||
        (!vendInvoiceInfoTable.isNonPO() && PurchPrepayTable::findPurchId(vendInvoiceInfoTable.PurchId).PrepayApplicationRemaining > 0))
    {
        vendInvoiceInfoTable_ds.allowDelete(false);
    }
    else
    {
        vendInvoiceInfoTable_ds.allowDelete(true);
    }

    return ret;
}

public int active()
{
    int ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

void clicked()
{
    super();

    if (vendInvoiceInfoTable.Log)
    {
        infolog.import(info::infoStr2Con(VendInvoiceInfoTable.Log));
    }
    else
    {
        info("@SYS19186");
    }
}

void clicked()
{
    Args args;
    MenuFunction menuFunction;
    VendInvoiceJour vendInvoiceJour;

    super();

    vendInvoiceJour = vendInvoiceJour::findSourceDocumentHeader(vendInvoiceInfoTable.SourceDocumentHeader);

    args = new Args();
    args.record(vendInvoiceJour);

    menuFunction = new menuFunction(menuitemDisplayStr(VendInvoiceJournal), MenuItemType::Display);

    if (!menuFunction || !menuFunction.checkAccessRights())
    {
        // Access rights are not sufficient to use this function
        throw error("@SYS81158");
    }

    menuFunction.run(args);
}
