public void createRecord(str _formDataSourceName, boolean _append = false)
{
    Map selectedFormDataSources = new Map(Types::String, Types::String);

    if (_formDataSourceName == vendInvoiceTemplate_ds.name())
    {
        super(_formDataSourceName, _append);
    }
    else if (_formDataSourceName == tmplLine_ds.name())
    {
        selectedFormDataSources.insert(tmplLine_ds.name(), tableStr(VendInvoiceStandardLineTemplate));
        tmplLine_ds.createTypes(selectedFormDataSources, true);
    }

}

public void setFieldAccess()
{
    boolean isProjectSelected;

    isProjectSelected = tmplLine_VendInvoiceStandardLineTemplate.ProjId != '';

    vendInvoiceDistributionTemplateButton.enabled(tmplLine_VendInvoiceStandardLineTemplate.AmountCur != 0);

    tmplLine_VendInvoiceStandardLineTemplate_ds.object(fieldNum(VendInvoiceStandardLineTemplate, ProjCategoryId)).mandatory(isProjectSelected);
}

public void clearFields()
{
    tmplLine_VendInvoiceStandardLineTemplate_ds.object(fieldNum(VendInvoiceStandardLineTemplate, Quantity)).setValue('');
    tmplLine_VendInvoiceStandardLineTemplate_ds.object(fieldNum(VendInvoiceStandardLineTemplate, AmountDetails)).setValue('');
    tmplLine_VendInvoiceStandardLineTemplate_ds.object(fieldNum(VendInvoiceStandardLineTemplate, LineTxt)).setValue('');
}

public int active()
{
    int ret;

    ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.setFieldAccess();
    }
    return ret;
}

public void initValue()
{
    super();
    tmplLine.TaxGroup = vendInvoiceTemplate.TaxGroup;

    tmplLine_VendInvoiceStandardLineTemplate.AccountingDistributionTemplate = vendInvoiceTemplate.AccountingDistributionTemplate;
    tmplLine_VendInvoiceStandardLineTemplate.DefaultDimension = vendInvoiceTemplate.DefaultDimension;
}

public void executeQuery()
{
    tmplLine_ds.query().dataSourceName(tmplLine_ds.name()).addRange(
        fieldNum(VendInvoiceLineTemplate, InstanceRelationType)).value(SysQuery::value(DictTable::getRelationTypeFromTableName(tableStr(VendInvoiceStandardLineTemplate))));

    super();
    tmplLine_ds.insertIfEmpty(true);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    InventLookup lookup = InventLookupItemIdNonStocked::newLookupCtrl(_formControl);

    lookup.run();
}

public void dialogClosed(xFormRun _formRun)
{
    Object formRunObj;
    VendInvoiceTemplateName vendInvoiceTemplateName;
    Description vendInvoiceTemplateDescription;

    super(_formRun);

    if (_formRun.closedOk())
    {
        formRunObj = _formRun;
        if (formHasMethod(_formRun, identifierstr(parmVendInvoiceTemplateName)))
        {
            vendInvoiceTemplateName = formRunObj.parmVendInvoiceTemplateName();
        }
        if (formHasMethod(_formRun, identifierstr(parmVendInvoiceTemplateDescription)))
        {
            VendInvoiceTemplateDescription = formRunObj.parmVendInvoiceTemplateDescription();
        }

        VendInvoiceTemplate::copyVendInvoiceTemplate(VendInvoiceTemplate, vendInvoiceTemplateName, vendInvoiceTemplateDescription);
        VendInvoiceTemplate_ds.research(true);
    }
}

