[Form]
public class VendInvoiceViolation extends FormRun
{
    RecId vendInvoiceInfoTableRecId;

}

public boolean canClose()
{
    boolean allowClose;
    VendInvoiceInfoTable localVendInvoiceInfoTable;
    SysPolicySourceDocumentRuleViolation localSysPolicySourceDocumentRuleViolation;
    VendInvoiceInfoLine localVendInvoiceInfoLine;

    allowClose = super();

    // Do not delete violations if the form is open from VendInvoiceMatchingDetails form
    // because violation will be deleted when vendInvoiceMatchingDetails form will be close.
    if (allowClose && element.args().parm() != formStr(VendInvoiceMatchingDetails))
    {
        localVendInvoiceInfoTable = VendInvoiceInfoTable::findRecId(vendInvoiceInfoTableRecId);

        while select forupdate localSysPolicySourceDocumentRuleViolation
            exists join localVendInvoiceInfoLine
                where localVendInvoiceInfoLine.SourceDocumentLine == localSysPolicySourceDocumentRuleViolation.SourceDocumentLine
        {
            localSysPolicySourceDocumentRuleViolation.delete();
        }

        delete_from localSysPolicySourceDocumentRuleViolation where
            localSysPolicySourceDocumentRuleViolation.SourceDocumentHeader == localVendInvoiceInfoTable.SourceDocumentHeader;
    }

    return allowClose;
}

public void init()
{
    VendInvoicePolicyValidation vendInvoicePolicyValidation;
    VendInvoiceInfoLine localVendInvoiceInfoLine;
    Common common;

    super();

    //Initialize splitter
    common = element.args().record();

    if (common.RecId)
    {
        vendInvoicePolicyValidation = new VendInvoicePolicyValidation();

        switch (common.TableId)
        {
            case tableNum(VendInvoiceInfoTable):
                vendInvoicePolicyValidation.parmVendInvoiceInfoTable(common as VendInvoiceInfoTable);
                break;
            case tableNum(VendInvoiceInfoLine):
                localVendInvoiceInfoLine = common as VendInvoiceInfoLine;
                vendInvoicePolicyValidation.parmVendInvoiceInfoTable(localVendInvoiceInfoLine.vendInvoiceInfoTable());
                break;
        }

        vendInvoiceInfoTableRecId = vendInvoicePolicyValidation.parmVendInvoiceInfoTable().RecId;

        // Do not record violations if the form is open from VendInvoiceMatchingDetails form
        // because violation is already recorded by VendInvoiceMatchingDetails form.
        if (element.args().parm() == formStr(VendInvoiceMatchingDetails))
        {
            vendInvoicePolicyValidation.parmRecordViolation(false);
        }

        if (!vendInvoicePolicyValidation.checkVendorInvoicePolicyValidation())
        {
            tabPageHeader.visible(false);
            tabPageLines.visible(false);
        }
    }
}

public int active()
{
    SysPolicyTypeSourceDocumentQuery localSysPolicyTypeSourceDocumentQuery;
    int ret = super();

    if (ret)
    {
        select SourceRelationType from localSysPolicyTypeSourceDocumentQuery
           where localSysPolicyTypeSourceDocumentQuery.RecId == sysPolicySourceDocumentRuleType1.PolicyTypeSourceDocumentQuery;

        switch (tableName2id(localSysPolicyTypeSourceDocumentQuery.SourceRelationType))
        {
            case tableNum(VendInvoiceInfoTable), tableNum(MarkupTable) :
                tabPageHeader.visible(true);
                tabPageLines.visible(false);
                break;

            case tableNum(VendInvoiceInfoLine) :
                tabPageHeader.visible(false);
                tabPageLines.visible(true);
                vendInvoiceInfoLine_ds.filter(fieldNum(VendInvoiceInfoLine, SourceDocumentLine), int642str(sysPolicySourceDocumentRuleViolation.SourceDocumentLine));
                break;
        }

        sysPolicySourceDocumentRuleType1_name.text(localSysPolicyTypeSourceDocumentQuery.SourceRelationType);
    }

    return ret;
}

void clicked()
{
    Args args;
    MenuFunction ruleMenuFunction;

    args = new Args();
    args.caller(element);
    args.record(sysPolicyRule);

    args.parm(int642str(sysPolicyRuleType.RecId));
    args.name(sysPolicyRuleType.RuleFormName);

    ruleMenuFunction = new MenuFunction(sysPolicyRuleType.RuleFormName, MenuItemType::Action);
    ruleMenuFunction.run(args);
}
