/// <summary>
/// Validates access to the form when it is initially opened.
/// </summary>
public void init()
{
    VendInvoiceInfoTable    localVendInvoiceInfoTable;

    // Window must be called with parameters
    if (!element.args().caller())
    {
         throw error("@SYS22539");
    }

    super();

    if (element.args().dataset() == tableNum(VendInvoiceInfoTable))
    {
        purchEditLines = element.args().caller();
        localVendInvoiceInfoTable = element.args().record();
        element.setPurchTotalValues(localVendInvoiceInfoTable);
        element.setXmlMatchingValues(localVendInvoiceInfoTable);
    }
}

private void setPurchTotalValues(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    purchTotals = PurchTotals::newParmTable(_vendInvoiceInfoTable,
                                            PurchUpdate::All,
                                            _vendInvoiceInfoTable.ParmId,
                                            '',
                                            _vendInvoiceInfoTable.Ordering);

    purchTotals.calc();

    if (purchTotals.recordListPurchLine().len() == 0 && purchTotals.recordSortedListLine().len() == 0)
    {
        throw error("@SYS4010114");
    }
    else
    {
        element.updateInvoiceTotals();
    }
}

private void setXmlMatchingValues(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    VendInvoiceInfoTable_W              vendInvoiceInfoTableW;
    EFDocumentReceivedXML_BR            receivedXml;
    EFDocumentReceivedXmlView_BR        viewXml;

    select RecId from receivedXml
        exists join vendInvoiceInfoTableW
            where vendInvoiceInfoTableW.AccessKey_BR == receivedXml.Accesskey
                && vendInvoiceInfoTableW.vendInvoiceInfoTable == _vendInvoiceInfoTable.RecId;

    if (receivedXml.RecId)
    {
        viewXml = EFDocumentReceivedXmlView_BR::findOrCreate(receivedXml.RecId);
    }

    if (!viewXml.RecId)
    {
        throw error("@SYS4010064", '', SysInfoAction_MenuFunction::newMenuItem(menuitemDisplayStr(EFDocumentReceivedXML_BR), MenuItemType::Display));
    }
}

private void updateApprovalStatusLabel()
{
    if (hasAmountDivergences == false)
    {
        TextApprovalStatus.text("@SYS4004091");
    }
    else if (efDocumentReceivedXML_BR.hasApprovedDivergences())
    {
        TextApprovalStatus.text("@SYS4004094");
    }
    else
    {
        TextApprovalStatus.text("@SYS4004092");
    }
}

public void updateDesign()
{
    element.updateWarnings();

    ButtonApproveDivergences_BR.enabled(hasAmountDivergences && !efDocReceivedXmlApprovedDivergences_BR.RecId);
    ButtonShowApprovedDivergences_BR.enabled(efDocReceivedXmlApprovedDivergences_BR.RecId);
    ButtonRequestNewApproval_BR.enabled(efDocReceivedXmlApprovedDivergences_BR.RecId);
    element.updateApprovalStatusLabel();
}

public void updateInvoiceTotals()
{
    PurchTotals_ICMSTaxAmount.realValue(purchTotals.purchTaxTotalICMS_BR());
    PurchTotals_ICMSBaseAmount.realValue(purchTotals.purchBaseTotalICMS_BR());
    PurchTotals_ICMSSTTaxAmount.realValue(purchTotals.purchTaxTotalICMSST_BR());
    PurchTotals_ICMSSTBaseAmount.realValue(purchTotals.purchBaseTotalICMSST_BR());
    PurchTotals_IPITaxAmount.realValue(purchTotals.purchTaxTotalIPI_BR());

    PurchTotals_MarkupFreightAmount.realValue(purchTotals.markupFreightAmountMST_BR());
    PurchTotals_MarkupInsuranceAmount.realValue(purchTotals.markupInsuranceAmountMST_BR());
    PurchTotals_MarkupOtherAmount.realValue(purchTotals.markupOthersAmountMST_BR());

    PurchTotals_TotalProductAmount.realValue(purchTotals.totalLinesGrossAmountMST_BR());
    PurchTotals_TotalAmount.realValue(purchTotals.purchTotalAmountMST_BR());
}

public void updateWarnings()
{
    this.matchingVarianceCnpj();
    this.matchingVarianceIe();

    hasAmountDivergences = this.matchingVarianceTotalAmount() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceTotalProducts() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceIpiAmount() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceIcmsAmount() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceIcmsBaseAmount() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceIcmsSTAmount() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceIcmsSTBaseAmount() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceFreight() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceInsurance() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
    hasAmountDivergences = this.matchingVarianceOtherCharges() == LastMatchVarianceOptions::Warning || hasAmountDivergences;
}

