        queryBuildEFDocumetReceived.clearDynalinks();
        queryBuildEFDocumetReceived.addDynalink(
            fieldNum(EFDocumentReceivedXML_BR, AccessKey),
            VendInvoiceInfoTable_W,
            fieldNum(VendInvoiceInfoTable_W, AccessKey_BR));
    }
}

