[Form]
public class VendOpenInvoicesListPage extends FormRun
{
    boolean insideSetButtonAccess;
    QueryBuildRange criteriaCredit;

}

void allowEnable()
{
    boolean enableButtons;

    if (vendTrans_ds.getFirst())
    {
        enableButtons = true;
    }
    //Enable/Disable any non-new related buttons
    openTransactionEditing.enabled(enableButtons);
    closedTransactionEditing.enabled(enableButtons);
    paymentJournal.enabled(enableButtons);
    voucher.enabled(enableButtons);
    transactions.enabled(enableButtons);
    miscellaneousCharges.enabled(enableButtons);
    adjustments.enabled(enableButtons);
    postedSalesTax.enabled(enableButtons);
    printManagement.enabled(enableButtons);
    copy.enabled(enableButtons);
    original.enabled(enableButtons);
}

public void init()
{
    super();
    element.setButtonAccess();

    //Make VendPaymentGroup visible if the AP Payment group feature is enabled
    vendTrans_VendPaymentGroup.visible(VendParameters::find().VendPaymentGroupingEnable == NoYes::Yes);

    if (SecurityRights::construct().hasMenuItemAccess(SecurableType::MenuItemAction, menuitemactionstr(MarkupAdjustment)))
    {
        adjustments.visible(true);
    }
    else
    {
        adjustments.visible(false);
    }

    criteriaCredit = VendTrans_DS.query().dataSourceTable(tableNum(VendTrans)).findRange(fieldNum(VendTrans, AmountCur));

    switch (element.args().menuItemName())
    {
        case menuitemdisplaystr(VendOpenInvoicesDueTodayListPage):
            vendTransOpen_TransDate.visible(false);
            vendTransOpen_DueDatePastDue.visible(false);
            this.design().caption("@SYS110038");
            break;

        case menuitemdisplaystr(VendOpenInvoicesPastDueListPage):
            vendTransOpen_TransDate.visible(false);
            vendTransOpen_DueDatePastDue.visible(true);
            vendTransOpen_DueDate.visible(false);
            this.design().caption("@SYS110022");
            break;
    }

    // <GIN>
    vendTransOpen_TDSAmount_IN.visible(TaxWithholdParameters_IN::isTDSActivate());
    vendTransOpen_TCSAmount_IN.visible(TaxWithholdParameters_IN::isTCSActivate());
    // </GIN>

    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        Maintain_PSN.visible(true);
        VendDirectiveForm_PSN.visible(true);
    }
    else
    {
        Maintain_PSN.visible(false);
        VendDirectiveForm_PSN.visible(false);
    }
}

/// <summary>
/// Loads the form object depending upon the values passed.
/// </summary>
/// <param name="_object">
/// The object where the event occured.
/// </param>
/// <param name="_args">
/// The object holding the form name which is to be called.
/// </param>
/// <remarks>
/// This method holds the common functionality and is called by jumpRefVend() method.
/// </remarks>
void jumpRefFormCall(Object _object, Args _args)
{
    FormRun formRun;
    Args args = _args;

    args.caller(_object);
    args.parm(enum2str(PaymHold::No));
    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.detach();
}

/// <summary>
/// Provides Go To Main Table functionality depending upon the Vendor hold values e.g. Vendor, Invoice
/// </summary>
/// <param name="_object">
/// The object where the event occured.
/// </param>
/// <param name="_paymHold">
/// The string value holds the possible values e.g. Vendor, Invoice ;optional.
/// </param>
/// <remarks>
/// The common functionality is split into jumpRefFormCall(_object,args) method
/// which is called at the end.
/// </remarks>
void jumpRefVend(Object _object, PaymHold _paymHold = PaymHold::No)
{
    Args args;
    PaymHold paymHold = _paymHold;

    switch (paymHold)
    {
        case PaymHold::Vendor:
            args = new Args(formStr(VendTable));
            args.menuItemName(menuItemDisplayStr(VendTable));
            args.record(VendTable::find(vendTable.AccountNum,false));
            element.jumpRefFormCall(_object,args);
            break;

        case PaymHold::Invoice:
            args = new Args(formStr(VendTrans));
            args.menuItemName(menuItemDisplayStr(VendTrans));
            args.record(VendTrans::find(vendTrans.RecId));
            element.jumpRefFormCall(_object,args);
            break;

        default:
            args = new Args(formStr(VendTable));
            args.menuItemName(menuItemDisplayStr(VendTable));
            args.record(VendTable::find(vendTable.AccountNum,false));
            element.jumpRefFormCall(_object,args);

            args = new Args(formStr(VendTrans));
            args.menuItemName(menuItemDisplayStr(VendTrans));
            args.record(VendTrans::find(vendTrans.RecId));
            element.jumpRefFormCall(_object,args);
            break;
    }
}

void printCopyOriginal(PrintCopyOriginal _action, str _copy, str _original, str _originalPrint, Args _args)
{
    switch (_action)
    {
        //print the copy, original, or originalPrint depending upon user selection.
        case PrintCopyOriginal::Copy :
            _args.name(_copy);
            new MenuFunction(_copy, MenuItemType::Output).run(_args);
            break;
        case PrintCopyOriginal::Original :
            _args.name(_original);
            new MenuFunction(_original, MenuItemType::Output).run(_args);
            break;
        case PrintCopyOriginal::OriginalPrint :
            _args.name(_originalPrint);
            new MenuFunction(_originalPrint, MenuItemType::Output).run(_args);
            break;
    }
}

void printInvoice(PrintCopyOriginal _action)
{
    VendTransOpen vendTransOpenLocal;
    VendTrans vendTransLocal;
    VendInvoiceJour vendInvoiceJour;
    Args args = new Args();

    vendTransOpenLocal = vendTransOpen_ds.getFirst(1, false);

    if (!vendTransOpenLocal)
    {
        //No marked records, use selected record
        vendTransOpenLocal = vendTransOpen;
    }

    while (vendTransOpenLocal)
    {
        vendTransLocal = VendTrans::find(vendTransOpenLocal.RefRecId);
        switch (vendTransLocal.TransType)
        {
            // printing is multi-select enabled so for all selected records, print the appropriate
            // report depending upon the transaction type.
            case LedgerTransType::Purch :
                // Purch order invoice
                vendInvoiceJour = VendInvoiceJour::findFromVendTrans(vendTransLocal.Invoice, vendTransLocal.TransDate, vendTransLocal.AccountNum);
                args.record(vendInvoiceJour);
                element.printCopyOriginal(_action, menuitemOutputStr(PurchInvoiceCopy), menuitemOutputStr(PurchInvoiceOriginal), menuitemOutputStr(PurchInvoice), args);
                break;
            case LedgerTransType::None :
                //  General Journal
                vendInvoiceJour = VendInvoiceJour::findFromVendTrans(vendTransLocal.Invoice, vendTransLocal.TransDate, vendTransLocal.AccountNum);
                args.record(vendInvoiceJour);
                element.printCopyOriginal(_action, menuitemOutputStr(PurchInvoiceCopy), menuitemOutputStr(PurchInvoiceOriginal), menuitemOutputStr(PurchInvoice), args);
                break;
        }
        vendTransOpenLocal = vendTransOpen_ds.getNext();
    }
}

public void run()
{
    super();
}

void setButtonAccess()
{
    VendInvoiceJour vendInvoiceJourLocal;

    element.allowEnable();
    //Disable Adjustment & Miscellaneuos charge button if Records is of type Approval Journal Invoice or Invoice Journal, or GeneralJournal)
    miscellaneousCharges.enabled(vendTrans.TransType == LedgerTransType::Purch);
    adjustments.enabled(vendTrans.TransType == LedgerTransType::Purch);

    vendInvoiceJourLocal = VendInvoiceJour::findFromVendTrans(vendTrans.Invoice, vendTrans.TransDate, vendTrans.AccountNum);
    if (!vendInvoiceJourLocal)
    {
        printManagement.enabled(false);
        copy.enabled(false);
        original.enabled(false);
    }
    this.setButtonAccessMultiSelect();
}

void setButtonAccessMultiSelect()
{
    VendInvoiceJour vendInvoiceJourLocal;
    VendTransOpen vendTransOpenLocal;
    boolean shouldBeEnabled = true;
    boolean recordsMarked = false;
    VendTrans vendTransLocal;

    // go through all marked records
    vendTransOpenLocal = vendTransOpen_ds.getFirst(1, false);
    if (!vendTransOpenLocal)
    {
        //No marked records, use selected record
        vendTransOpenLocal = vendTransOpen;
    }

    shouldBeEnabled = true;
    while (vendTransOpenLocal && shouldBeEnabled)
    {
        // if one of the rows require print/send/view disabled then break out of the
        // loop since the buttons are to be disabled if even one selection requires it
        recordsMarked = true;

        select TransType, Invoice, TransDate, AccountNum from vendTransLocal
            where vendTransLocal.RecId == vendTransOpenLocal.RefRecId;

        shouldBeEnabled = (vendTransLocal.TransType == LedgerTransType::None ||
                            vendTransLocal.TransType == LedgerTransType::Vend ||
                            vendTransLocal.TransType == LedgerTransType::Purch);

        vendInvoiceJourLocal = VendInvoiceJour::findFromVendTrans(
            vendTransLocal.Invoice,
            vendTransLocal.TransDate,
            vendTransLocal.AccountNum);

        if (!vendInvoiceJourLocal)
        {
            shouldBeEnabled = false;
        }

        vendTransOpenLocal = vendTransOpen_ds.getNext();
    }

    printManagement.enabled(shouldBeEnabled);
    copy.enabled(shouldBeEnabled);
    original.enabled(shouldBeEnabled);
}

/// <summary>
/// Sets up the <c>QueryFilter</c> based on the blocked status on vendTable.
/// </summary>
protected void setVendorBlockedStatusQueryFilter()
{
    // Apply criteria similar to the VendTrans.displayPaymentHold() display method
    QueryBuildDataSource vendTransqbds = VendTrans_DS.query().dataSourceTable(tableNum(VendTrans));
    QueryBuildDataSource vendTableqbds = VendTable_DS.query().dataSourceTable(tableNum(VendTable));

    QueryFilter qf = VendTrans_ds.query().addQueryFilter(vendTransqbds, fieldStr(VendTrans, TransDate));

    // if ((VendTable.Blocked == All or Payment)
    //   and (VendTable.BlockedReleaseDate == dateNull or VendTable.BlockedReleaseDate > VendTrans.TransDate))
    // or (VendTrans.InvoiceReleaseDate >= VendTrans.TransDate)
    qf.value(strFmt('(((%8.%1 = %2) || (%8.%1 = %3)) && ((%8.%4 = %5) || (%8.%4 > %9.%6))) || (%9.%7 >= %9.%6)',
                fieldStr(VendTable, Blocked),
                enum2Int(CustVendorBlocked::All),
                enum2Int(CustVendorBlocked::Payment),
                fieldStr(VendTable, BlockedReleaseDate),
                queryValue(dateNull()),
                fieldStr(VendTrans, TransDate),
                fieldStr(VendTrans, InvoiceReleaseDate),
                vendTableqbds.name(),
                vendTransqbds.name()));
}

/// <summary>
/// Applies query filters for Power BI reports drill-down
/// </summary>
private void setBIAutomationStatusQueryFilter()
{
    if (element.args().parmEnumType() == enumNum(VendInvoiceAutomationBIGraph))
    {
        VendInvoiceAutomationBIReportHandler::buildQueryForOpenVendorInvoicesListPage(VendTrans_ds.query(), element.args().parmObject() as PBIReportSelectedData, element.args().parmEnum());
    }
}

