display AmountCur amountCur(VendTransOpen _vendTransOpen)
{
    // use display method to prevent the debit/credit formatting
    return _vendTransOpen.AmountCur;
}

public void markChanged()
{
    super();
    element.setButtonAccess();
}

public void delete()
{
    super();
    element.setButtonAccess();
}

public int active()
{
    int ret = super();
    // enable/disable logic
    if (!this.anyMarked() && !insideSetButtonAccess)
    {
        insideSetButtonAccess = true; //to prevent recursive calls to setButtonAccessMultiSelect()
        element.setButtonAccess();
    }
    else
    {
        insideSetButtonAccess = false;
    }
    return ret;
}

public void executeQuery()
{
    if (criteriaCredit)
    {
        switch (includeCreditNotesCheckBox.value())
        {
            case 1:
                criteriaCredit.value(strFmt('((%1 <= %2) || (%3 != %4))', fieldStr(VendTrans, AmountCur), queryValue(0), fieldStr(VendTrans, Invoice), SysQuery::valueEmptyString()));
                break;
            case 0:
                criteriaCredit.value("..0");
                break;
        }
    }

    if (element.args().menuItemName() == menuItemDisplayStr(VendOpenInvoicesPaymentHoldListPage))
    {
        element.setVendorBlockedStatusQueryFilter();
    }
    else if (element.args().menuItemName() == menuItemDisplayStr(VendInvoiceAutomationBIOpenInvoicesListPage))
    {
        element.setBIAutomationStatusQueryFilter();
    }

    super();
    element.setButtonAccess();
}

public void clicked()
{
    Args args = new Args();
    VendInvoiceJour vendInvoiceJour;

    switch (vendTrans.TransType)
    {
        case LedgerTransType::Vend :
        case LedgerTransType::Purch :
            vendInvoiceJour = vendInvoiceJour::findFromVendTrans(vendTrans.Invoice, vendTrans.TransDate, vendTrans.AccountNum);

            // if a markuptrans record exists then the only way to get the markuptrans form
            // to display it properly is to use vendInvoiceJour as the calling record.
            // If it doesn't exist, then the markupTrans form gives us problems so we'll send
            // in VendTrans as the caller. This will effectively show an empty window.
            if (vendInvoiceJour && (MarkupTrans::exist(vendInvoiceJour.TableId, vendInvoiceJour.RecId)))
            {
                args.record(vendInvoiceJour);
            }
            else
            {
                args.record(vendTrans);
            }

            args.caller(this);
            args.name(formStr(VendOpenInvoicesListPage));
            new MenuFunction(menuitemDisplayStr(MarkupTrans_HeadingPurchAction), MenuItemType::Display).run(args);
            break;
    }
}

public void clicked()
{
    // There is no relationship between TaxTrans and CustTransOpen.
    // The fields to create a relationship between these two tables aren't
    // in VendTransOpen, we have to go through VendTrans.
    Args args = new Args(classStr(MarkupAdjustment));
    VendInvoiceJour vendInvoiceJour = VendInvoiceJour::findFromVendTrans(vendTrans.Invoice, vendTrans.TransDate, vendTrans.AccountNum);
    args.record(vendInvoiceJour);
    args.caller(element);

    new MenuFunction(menuitemActionStr(MarkupAdjustment), MenuItemType::Action).run(args);
}

