/// <summary>
/// Incorporates Go To Main Table functionality.
/// </summary>
/// <remarks>
/// Opens <c>VendTable</c> or <c>VendTrans</c> forms depending upon the field value.
/// </remarks>
public void jumpRef()
{
    PaymHold paymHold;

    paymHold = str2enum(paymHold, this.valueStr());
    super();

    element.jumpRefVend(this, paymHold);
}
