public void initFromCommon()
{
    // Validate that the calling record exists & is the VendTable
    if (caller.RecId == 0)
    {
        throw error("@SYS22904");
    }
    else if (element.checkCallerTableIsInvalid())
    {
        throw error(Error::wrongUseOfFunction(this.name()));
    }
    else
    {
        vendTableCaller = caller;

        // This gets called from linkActive so this is here to ensure the query range is always up to date.
        qbrAccountNum.value(strFmt('(((%1 == \"%2\") && (%4 == \"%5\")) || ((%3 == \"%2\") && (%6 == \"%5\")))',
            fieldStr(VendSettlement, AccountNum),
            SysQuery::value(vendTableCaller.AccountNum),
            fieldStr(VendSettlement, OffsetAccountNum),
            fieldStr(VendSettlement, TransCompany),
            SysQuery::value(vendTableCaller.company()),
            fieldStr(VendSettlement, OffsetCompany)));
        qbrAccountNum.status(RangeStatus::Hidden);

        if (vendTransRecId)
        {
            qbrTransRecId.value(strFmt('((%1 == %2) || (%3 == %2))',
            fieldStr(VendSettlement, TransRecId),
            SysQuery::value(vendTransRecId),
            fieldStr(VendSettlement, OffsetRecId)));
            qbrTransRecId.status(RangeStatus::Hidden);
        }
        else
        {
            settlementDateController.applyRange();
        }
    }
    // <GEERU><GEEPL>
    if (TaxParameters::find().ReverseSettlementDatePrinciple_W || countryRegion_RUPL || usePostingLog)
    {
        vendReversePosting    = CustVendReversePosting::construct(vendTableCaller, null);
        parmSettlePostingType = vendReversePosting.parmDatePrinciple();
        parmSettlePostingDate = vendReversePosting.parmSaveDate_W();
    }
    // </GEERU></GEEPL>

    specTransManager = SpecTransManager::newRefTableId(vendTableCaller, tableNum(VendSettlement), true);
}

/// <summary>
///     Check the caller table is invalid or not.
/// </summary>
/// <returns>
///     true if the caller table is not <c>VendTable</c> table; otherwise, false.
/// </returns>
protected boolean checkCallerTableIsInvalid()
{
    return caller.TableId != tableNum(VendTable);
}

public boolean isPrepaymentApplication()
{
    VendTrans vendTransLocal;
    SpecTrans specTransLocal;
    VendSettlement vendSettlementLocal;

    select firstonly crosscompany RecId from vendTransLocal
        where vendTransLocal.TransType == LedgerTransType::PurchAdvanceApplication
        exists join vendSettlementLocal
            where vendSettlementLocal.TransCompany == vendTransLocal.DataAreaId
                && vendSettlementLocal.TransDate == vendTransLocal.TransDate
                && vendSettlementLocal.TransRecId == vendTransLocal.RecId
        exists join specTransLocal
            where specTransLocal.RefCompany == vendSettlementLocal.DataAreaId
                && specTransLocal.RefTableId == vendSettlementLocal.TableId
                && specTransLocal.RefRecId == vendSettlementLocal.RecId
                && specTransLocal.SpecCompany == vendTableCaller.company()
                && specTransLocal.SpecTableId == vendTableCaller.TableId
                && specTransLocal.SpecRecId == vendTableCaller.RecId;

    if (vendTransLocal)
    {
        checkFailed("@SYS343103");
        return true;
    }

    return false;
}

public void updateDataSource()
{
    Query formQuery;
    QueryBuildDataSource qbdsVendTrans;
    QueryBuildDataSource qbdsVendSettlement;
    container companies;
    int i;

    formQuery = vendTrans_ds.query();

    // Get current shared service companies
    companies = CustVendOpenTransManager::findSharedServiceCompanies(curext());

    for (i = 1; i <= conLen(companies); i++)
    {
        formQuery.addCompanyRange(conPeek(companies, i));
    }

    // Clear dynalinks for the VendTrans data source so that the caller's dataAreaId doesn't restrict the query
    qbdsVendTrans = formQuery.dataSourceTable(tableNum(VendTrans));
    qbdsVendTrans.clearDynalinks();

    // Add range on CanBeReversed flag
    qbdsVendSettlement = formQuery.dataSourceTable(tableNum(VendSettlement));
    criteriaAllNew = qbdsVendSettlement.addRange(fieldNum(VendSettlement, CanBeReversed));
    criteriaAllNew.value(queryValue(NoYes::Yes));

    // Add range to include all accounts from the shared service boundary
    qbrAccountNum = qbdsVendSettlement.addRange(fieldNum(VendSettlement, AccountNum));
    qbrTransRecId = qbdsVendSettlement.addRange(fieldNum(VendSettlement, TransRecId));

    settlementDateController.addRange();
    settlementDateController.addSortField();

    // SpecTransMarked is only used for filtering marked records
    this.setSpecTransDS(false);
}

public boolean validateSettlePostingDate(SettleDatePrinc _settlePostingType, TransDate _settlePostingDate)
{
    boolean isValid = true;

    if (_settlePostingDate != dateNull())
    {
        isValid = vendSettlement1.custVendSettlementInterface().validateReversalSettlementDate(vendTableCaller, _settlePostingDate);
    }
    // The settlement posting date cannot be blank if the type is selected date
    else if (_settlePostingType == SettleDatePrinc::SelectDate)
    {
        isValid = checkFailed("@SYS117112");
    }

    return isValid;
}

