public edit NoYes markTrans(boolean _set, CustVendSettlement _vendSettlement, NoYes _markTrans)
{
    NoYes returnTransMarked;

    if (_set)
    {
        if (_markTrans == NoYes::Yes)
        {
            // Prompt the user with a dialog to remove marked transactions if a Promissory Note transaction is marked for reversal.
            if (vendtrans.PromissoryNoteID || VendTrans::findVoucherDate(vendtrans.LastSettleVoucher, vendtrans.LastSettleDate).PromissoryNoteID)
            {
                if (Box::yesNo("@SYS4000042", DialogButton::No) == DialogButton::No)
                {
                    return returnTransMarked;
                }
            }

            if (!specTransManager.existForOtherSpec(_vendSettlement.DataAreaId, _vendSettlement.TableId, _vendSettlement.RecId))
            {
                _vendSettlement.CustVendSettlement::markThisAndRelatedOffsets(specTransManager, vendTrans.CurrencyCode);

                // <GTH>
                if (TaxThaiGovCertificationFeatureChecker::isTaxWithholdEnabled())
                {
                    _vendSettlement.custVendSettlement::markRelatedTaxWithholdVoucher(
                        specTransManager,
                        vendTrans.Voucher,
                        vendTrans.TransDate);
                }
                // </GTH>

                reverseNew.enabled(true);
            }
            else
            {
                error("@SYS117108");
            }
        }
        else
        {
            _vendSettlement.CustVendSettlement::unmarkThisAndRelatedOffsets(specTransManager);

            // <GTH>
            if (TaxThaiGovCertificationFeatureChecker::isTaxWithholdEnabled())
            {
                _vendSettlement.custVendSettlement::unmarkRelatedTaxWithholdVoucher(
                    specTransManager,
                    vendTrans.Voucher,
                    vendTrans.TransDate);
            }
            // </GTH>

            if (specTransManager.getSpecTransCount() < 1)
            {
                reverseNew.enabled(false);
            }
        }

        vendTrans_ds.refreshEx(-1);
    }
    else
    {
        returnTransMarked = specTransManager.exist(_vendSettlement.DataAreaId, _vendSettlement.TableId, _vendSettlement.RecId);
    }

    return returnTransMarked;
}

