public void init()
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryFilter             queryFilter;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoIN, #isoJP, #isoRU ]))
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]) && element.args())
    {
        queryBuildDataSource = this.query().dataSourceTable(tableNum(VendTrans_W));
        queryFilter = this.query().findQueryFilter(queryBuildDataSource, fieldStr(VendTrans_W, BankCurrencyTransferId_RU));
        if (!queryFilter)
        {
            queryFilter = this.query().addQueryFilter(queryBuildDataSource, fieldStr(VendTrans_W, BankCurrencyTransferId_RU));
            queryFilter.status(RangeStatus::Hidden);
        }

        switch (element.args().menuItemName())
        {
            case menuitemDisplayStr(VendOpenTransReverseAction) :
                queryFilter.value(SysQuery::valueEmptyString());
                break;

            case menuitemDisplayStr(VendOpenTransBankReverse_RU) :
                queryFilter.value(SysQuery::valueNotEmptyString());
                break;

            default :
                queryFilter.value(SysQuery::valueUnlimited());
        }
    }
}

