public void clicked()
{
    super();

    vendSettlement1_ds.reread();

    if (vendSettlement1.CanBeReversed == NoYes::Yes)
    {
        ttsbegin;

        if (element.validateSettlePostingDate(parmSettlePostingType, parmSettlePostingDate)
            && !VendTrans::hasLCRelatedTransToBeReversed(vendTableCaller)
            && !element.isPrepaymentApplication())
        {
            if (VendTrans::reverseTransact(vendTableCaller, null, parmSettlePostingType, parmSettlePostingDate))
            {
                specTransManager.deleteAll();
                vendTrans_ds.executeQuery();
            }
        }

        ttscommit;
    }
    else
    {
        reverseNew.enabled(false);
        info("@CashManagement:TransactionAlreadyReversed");
    }

    vendSettlement1_ds.research();
    vendSettlement1_ds.refresh();

    element.close();
}

public void clicked()
{
    super();

    specTransManager.deleteAll();
    element.close();
}
