public void close()
{
    LedgerJournalTrans localLedgerJournalTrans;

    xSysLastValue::saveLast(this);

    ttsbegin;

    if (ledgerJournalTrans.RecId)
    {
        localLedgerJournalTrans = LedgerJournalTrans::findRecId(ledgerJournalTrans.RecId, true);

        if (manager.getSpecTransCount())
        {
            localLedgerJournalTrans.SettleVoucher= SettlementType::SelectedTransact;
        }
        else
        {
            localLedgerJournalTrans.SettleVoucher= VendParameters::find().AutoSettle ?
                                                   SettlementType::OpenTransact      :
                                                   SettlementType::None;
        }

        localLedgerJournalTrans.write();
    }

    ttscommit;

    super();
}

